                },
                "responses": {
                    "200": {
                        "description": "Successful Response",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "title": "Response Fictional Child Data Turner Fictional Child Data Post",
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/MeasurementObject"
                                    }
                                }
                            }
                        }
                    },
                    "422": {
