                    "gestation_weeks": {
                        "title": "Gestation Weeks",
                        "type": "integer"
                    },
                    "gestation_days": {
                        "title": "Gestation Days",
                        "type": "integer"
                    },
                    "estimated_date_delivery": {
                        "title": "Estimated Date Delivery",
                        "type": "string",
                        "format": "date"
                    },
                    "estimated_date_delivery_string": {
                        "title": "Estimated Date Delivery String",
                        "type": "string"
                    },
                    "sex": {
                        "title": "Sex",
                        "enum": [
                            "male",
                            "female"
                        ],
                        "type": "string"
                    }
                }
            },
            "BoneAge": {
                "title": "BoneAge",
                "type": "object",
                "properties": {
                    "bone_age": {
                        "title": "Bone Age",
                        "type": "number"
                    },
                    "bone_age_type": {
                        "title": "Bone Age Type",
                        "type": "string"
                    },
                    "bone_age_sds": {
                        "title": "Bone Age Sds",
                        "type": "number"
                    },
                    "bone_age_centile": {
                        "title": "Bone Age Centile",
                        "type": "number"
                    },
                    "bone_age_text": {
                        "title": "Bone Age Text",
                        "type": "string"
                    }
                }
            },
            "Centile": {
                "title": "Centile",
                "required": [
                    "sds",
                    "centile"
                ],
                "type": "object",
                "properties": {
                    "sds": {
                        "title": "Sds",
                        "type": "number"
                    },
