                        "items": {
                            "$ref": "#/components/schemas/Data"
                        }
                    }
                }
            },
            "CentileData": {
                "title": "CentileData",
                "required": [
                    "chronological_decimal_age_data",
                    "corrected_decimal_age_data"
                ],
                "type": "object",
                "properties": {
                    "chronological_decimal_age_data": {
                        "$ref": "#/components/schemas/ChronologicalDecimalAgeData"
                    },
                    "corrected_decimal_age_data": {
                        "$ref": "#/components/schemas/CorrectedDecimalAgeData"
                    }
                }
            },
            "Centile_Data": {
                "title": "Centile_Data",
                "required": [
                    "centile_data"
                ],
                "type": "object",
                "properties": {
                    "centile_data": {
                        "title": "Centile Data",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/ReferenceCreate"
                        }
                    }
                }
            },
            "ChartCoordinateRequest": {
                "title": "ChartCoordinateRequest",
                "required": [
                    "sex",
                    "measurement_method"
                ],
                "type": "object",
                "properties": {
                    "sex": {
                        "title": "Sex",
                        "enum": [
                            "male",
                            "female"
                        ],
                        "type": "string",
                        "description": "The sex of the patient, as a string value which can either be `male` or `female`. Abbreviations or alternatives are not accepted."
                    },
                    "measurement_method": {
                        "title": "Measurement Method",
                        "enum": [
                            "height",
                            "weight",
                            "ofc",
                            "bmi"
                        ],
                        "type": "string",
                        "description": "The type of measurement performed on the infant or child as a string which can be `height`, `weight`, `bmi` or `ofc`. The value of this measurement is supplied as the `observation_value` parameter. The measurements represent height **in centimetres**, weight *in kilograms**, body mass index **in kilograms/metre\u00b2** and occipitofrontal circumference (head circumference, OFC) **in centimetres**."
                    },
                    "is_sds": {
                        "title": "Is Sds",
                        "type": "boolean",
                        "description": "Boolean flag (default False) referring to centile_format. If custom lines requested as SDS, rather than as centiles, set this to True.",
                        "default": false
                    },
                    "centile_format": {
                        "title": "Centile Format",
                        "anyOf": [
                            {
                                "enum": [
                                    "cole-nine-centiles",
                                    "three-percent-centiles"
                                ],
                                "type": "string"
                            },
                            {
                                "type": "array",
                                "items": {
                                    "type": "number"
                                }
                            }
                        ],
                        "description": "Optional selection of centile format using 9 centile standard ['nine-centiles'], or older three-percent centile format ['three-percent-centiles'], or accepts a list of floats as a custom centile format e.g. [7/10/20/30/40/50/60/70/80/90/93]. Defaults to cole-nine-centiles",
                        "default": "cole-nine-centiles"
                    }
                }
            },
            "ChildObservationValue": {
                "title": "ChildObservationValue",
                "required": [
                    "measurement_method",
                    "observation_value"
                ],
                "type": "object",
                "properties": {
                    "measurement_method": {
                        "title": "Measurement Method",
                        "enum": [
                            "height",
                            "weight",
                            "ofc",
                            "bmi"
                        ],
                        "type": "string"
                    },
                    "observation_value": {
                        "title": "Observation Value",
                        "type": "number"
                    },
                    "observation_value_error": {
                        "title": "Observation Value Error",
                        "type": "string"
                    }
                }
            },
            "ChronologicalDecimalAgeData": {
                "title": "ChronologicalDecimalAgeData",
                "required": [
                    "age_type",
                    "lay_comment",
                    "clinician_comment"
                ],
                "type": "object",
                "properties": {
                    "x": {
                        "title": "X",
                        "type": "number"
                    },
                    "y": {
                        "title": "Y",
                        "type": "number"
                    },
                    "b": {
                        "title": "B",
                        "type": "number"
                    },
                    "bone_age_label": {
                        "title": "Bone Age Label",
                        "type": "string"
                    },
                    "events_text": {
                        "title": "Events Text",
                        "type": "array",
                        "items": {}
                    },
                    "bone_age_type": {
                        "title": "Bone Age Type",
                        "enum": [
                            "greulich-pyle",
                            "tanner-whitehouse-ii",
                            "tanner-whitehouse-iii",
                            "fels",
                            "bonexpert"
                        ],
                        "type": "string"
                    },
