      "ReplicateKeyResponse": {
        "type": "object",
        "properties": {
          "ReplicaKeyMetadata": {
            "allOf": [
              { "$ref": "#/components/schemas/KeyMetadata" },
              {
                "description": "Displays details about the new replica key, including its Amazon Resource Name (<a href=\"https://docs.aws.amazon.com/kms/latest/developerguide/concepts.html#key-id-key-ARN\">key ARN</a>) and <a href=\"https://docs.aws.amazon.com/kms/latest/developerguide/key-state.html\">key state</a>. It also includes the ARN and Amazon Web Services Region of its primary key and other replica keys."
              }
            ]
          },
          "ReplicaPolicy": {
            "allOf": [
              { "$ref": "#/components/schemas/PolicyType" },
              {
                "description": "The key policy of the new replica key. The value is a key policy document in JSON format."
              }
            ]
          },
          "ReplicaTags": {
            "allOf": [
