  "components": {
    "securitySchemes": {
      "bearerAuth": {
        "type": "http",
        "scheme": "bearer",
        "bearerFormat": "JWT"
      }
    },
    "responses": {
      "UnauthorizedError": {
        "description": "Unauthorized access to the API"
      },
      "ForbiddenError": {
        "description": "Not authorized to execute this operation"
      },
      "BackendError": {
        "description": "Unexpected Backend error, checks the Logs"
      },
      "EmailAlreadyUsed": {
        "description": "Email already used"
      },
      "NoBadgeError": {
        "description": "User doesn't have any badge"
      },
      "NotSupportedError": {
        "description": "Feature not supported"
      },
      "ResourceDoesNotExists": {
        "description": "Resource does not exist"
      },
      "TagHasTransactionsError": {
        "description": "Cannot change user or delete a tag having transactions"
      },
      "InvalidFileFormat": {
        "description": "Invalid file format"
      },
      "InvalidCSVHeaderFormat": {
        "description": "Invalid CSV header format"
      },
      "SiteAreaTreeError": {
        "description": "Circular dependency error in Site Area Tree"
      },
      "SiteAreaTreeSameSiteAreaError": {
        "description": "Site Area cannot have a parent of itslef"
      },
      "SiteAreaTreeSiteError": {
        "description": "Site Area must have the same Site as its parent"
      },
      "SiteAreaTreeSmartChargingError": {
        "description": "Site Area must have the same Smart Charging value as its parent"
      },
      "SiteAreaTreeNumberOfPhasesError": {
        "description": "Site Area must have the same Number of Phases as its parent"
      },
      "SiteAreaTreeChildrenMaxPower": {
        "description": "The total power of the child Site Areas must be lower than the one of their parents."
      },
      "SiteAreaTreeServeralActionNotSupported": {
        "description": "Multiple changes are currently not supported (eg. change of Site and Smart Charging.)"
      },
      "SiteAreaTreeVoltageError": {
        "description": "Site Area must have the same Voltage as its parent"
      },
      "SiteAreaHierarchyDependencyError": {
        "description": "Site Area has dependencies to other Site Areas"
      }
    },
