{
	"openapi": "3.0.0",
	"info": {
		"title": "grocy REST API",
		"description": "Authentication is done via API keys (header *GROCY-API-KEY* or same named query parameter), which you can manage [here](PlaceHolderManageApiKeysUrl).<br>Additionally requests from within the frontend are also valid (via session cookie).",
		"version": "xxx",
		"license": {
			"name": "grocy.info",
			"url": "https://grocy.info"
		}
	},
	"servers": [
		{
			"url": "xxx"
		}
	],
	"tags": [
		{
			"name": "Generic entity interactions",
			"description": "A limited set of entities are directly exposed for convenience"
		},
		{
			"name": "System"
		},
		{
			"name": "User management"
		},
		{
			"name": "Current user"
		},
		{
			"name": "Stock"
		},
		{
			"name": "Stock \"by-barcode\"",
			"description": "Some of the *Stock* routes, but access them by the products barcode instead of its id"
		},
		{
			"name": "Recipes"
		},
		{
			"name": "Chores"
		},
		{
			"name": "Batteries"
		},
		{
			"name": "Tasks"
		},
		{
			"name": "Calendar"
		},
		{
			"name": "Files"
		},
		{
			"name": "Print"
		}
	],
	"paths": {
		"/system/info": {
			"get": {
				"summary": "Returns information about the installed grocy version, PHP runtime and OS",
				"tags": [
					"System"
				],
				"responses": {
					"200": {
						"description": "An DbChangedTimeResponse object",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"properties": {
										"grocy_version": {
											"type": "object",
											"properties": {
												"Version": {
													"type": "string"
												},
												"ReleaseDate": {
													"type": "string",
													"format": "date"
												}
											}
										},
										"php_version": {
											"type": "string"
										},
										"sqlite_version": {
											"type": "string"
										}
									}
								}
							}
						}
					}
				}
			}
		},
		"/system/db-changed-time": {
			"get": {
				"summary": "Returns the time when the database was last changed",
				"tags": [
					"System"
				],
				"responses": {
					"200": {
						"description": "An DbChangedTimeResponse object",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/DbChangedTimeResponse"
								}
							}
						}
					}
				}
			}
		},
		"/system/config": {
			"get": {
				"summary": "Returns all config settings",
				"tags": [
					"System"
				],
				"responses": {
					"200": {
						"description": "Key/value pairs of config settings",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"description": "Key/value pairs of config settings"
								}
							}
						}
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			}
		},
		"/system/time": {
			"get": {
				"summary": "Returns the current server time",
				"tags": [
					"System"
				],
				"parameters": [
					{
						"$ref": "#/components/parameters/offsettime"
					}
				],
				"responses": {
					"200": {
						"description": "A TimeResponse object",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/TimeResponse"
								}
							}
						}
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			}
		},
		"/system/localization-strings": {
			"get": {
				"summary": "Returns all localization strings (in the by the user desired language)",
				"tags": [
					"System"
				],
				"responses": {
					"200": {
						"description": "The operation was successful",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"description": "A gettext JSON representation"
								}
							}
						}
					}
				}
			}
		},
		"/system/log-missing-localization": {
			"post": {
				"summary": "Logs a missing localization string",
				"description": "Only when MODE == 'dev', so should only be called then",
				"tags": [
					"System"
				],
				"requestBody": {
					"description": "A valid MissingLocalizationRequest object",
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"$ref": "#/components/schemas/MissingLocalizationRequest"
							}
						}
					}
				},
				"responses": {
					"204": {
						"description": "The operation was successful"
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			}
		},
		"/objects/{entity}": {
			"get": {
				"summary": "Returns all objects of the given entity",
				"tags": [
					"Generic entity interactions"
				],
				"parameters": [
					{
						"in": "path",
						"name": "entity",
						"required": true,
						"description": "A valid entity name",
						"schema": {
							"$ref": "#/components/schemas/ExposedEntity_NotIncludingNotListable"
						}
					},
					{
						"$ref": "#/components/parameters/query"
					},
					{
						"$ref": "#/components/parameters/order"
					},
					{
						"$ref": "#/components/parameters/limit"
					},
					{
						"$ref": "#/components/parameters/offset"
					}
				],
				"responses": {
					"200": {
						"description": "An entity object",
						"content": {
							"application/json": {
								"schema": {
									"type": "array",
									"items": {
										"oneOf": [
											{
												"$ref": "#/components/schemas/Product"
											},
											{
												"$ref": "#/components/schemas/Chore"
											},
											{
												"$ref": "#/components/schemas/Battery"
											},
											{
												"$ref": "#/components/schemas/Location"
											},
											{
												"$ref": "#/components/schemas/QuantityUnit"
											},
											{
												"$ref": "#/components/schemas/ShoppingListItem"
											},
											{
												"$ref": "#/components/schemas/StockEntry"
											},
											{
												"$ref": "#/components/schemas/ProductBarcode"
											}
										]
									}
								}
							}
						}
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					},
					"500": {
						"description": "The operation was not successful (possible errors are invalid field names or conditions in filter parameters provided)",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error500"
								}
							}
						}
					}
				}
			},
			"post": {
				"summary": "Adds a single object of the given entity",
				"tags": [
					"Generic entity interactions"
				],
				"parameters": [
					{
						"in": "path",
						"name": "entity",
						"required": true,
						"description": "A valid entity name",
						"schema": {
							"$ref": "#/components/schemas/ExposedEntity_NotIncludingNotEditable"
						}
					}
				],
				"requestBody": {
					"description": "A valid entity object of the entity specified in parameter *entity*",
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"oneOf": [
									{
										"$ref": "#/components/schemas/Product"
									},
									{
										"$ref": "#/components/schemas/Chore"
									},
									{
										"$ref": "#/components/schemas/Battery"
									},
									{
										"$ref": "#/components/schemas/Location"
									},
									{
										"$ref": "#/components/schemas/QuantityUnit"
									},
									{
										"$ref": "#/components/schemas/ShoppingListItem"
									},
									{
										"$ref": "#/components/schemas/StockEntry"
									},
									{
										"$ref": "#/components/schemas/ProductBarcode"
									}
								]
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "The operation was successful",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"properties": {
										"created_object_id": {
											"type": "integer",
											"description": "The id of the created object"
										}
									}
								}
							}
						}
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			}
		},
		"/objects/{entity}/{objectId}": {
			"get": {
				"summary": "Returns a single object of the given entity",
				"tags": [
					"Generic entity interactions"
				],
				"parameters": [
					{
						"in": "path",
						"name": "entity",
						"required": true,
						"description": "A valid entity name",
						"schema": {
							"$ref": "#/components/schemas/ExposedEntity_NotIncludingNotListable"
						}
					},
					{
						"in": "path",
						"name": "objectId",
						"required": true,
						"description": "A valid object id of the given entity",
						"schema": {
							"type": "integer"
						}
					}
				],
				"responses": {
					"200": {
						"description": "An entity object",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"oneOf": [
										{
											"$ref": "#/components/schemas/Product"
										},
										{
											"$ref": "#/components/schemas/Chore"
										},
										{
											"$ref": "#/components/schemas/Battery"
										},
										{
											"$ref": "#/components/schemas/Location"
										},
										{
											"$ref": "#/components/schemas/QuantityUnit"
										},
										{
											"$ref": "#/components/schemas/ShoppingListItem"
										},
										{
											"$ref": "#/components/schemas/StockEntry"
										},
										{
											"$ref": "#/components/schemas/ProductBarcode"
										}
									]
								}
							}
						}
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					},
					"404": {
						"description": "Object not found",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			},
			"put": {
				"summary": "Edits the given object of the given entity",
				"tags": [
					"Generic entity interactions"
				],
				"parameters": [
					{
						"in": "path",
						"name": "entity",
						"required": true,
						"description": "A valid entity name",
						"schema": {
							"$ref": "#/components/schemas/ExposedEntity_NotIncludingNotEditable"
						}
					},
					{
						"in": "path",
						"name": "objectId",
						"required": true,
						"description": "A valid object id of the given entity",
						"schema": {
							"type": "integer"
						}
					}
				],
				"requestBody": {
					"description": "A valid entity object of the entity specified in parameter *entity*",
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"oneOf": [
									{
										"$ref": "#/components/schemas/Product"
									},
									{
										"$ref": "#/components/schemas/Chore"
									},
									{
										"$ref": "#/components/schemas/Battery"
									},
									{
										"$ref": "#/components/schemas/Location"
									},
									{
										"$ref": "#/components/schemas/QuantityUnit"
									},
									{
										"$ref": "#/components/schemas/ShoppingListItem"
									},
									{
										"$ref": "#/components/schemas/StockEntry"
									},
									{
										"$ref": "#/components/schemas/ProductBarcode"
									}
								]
							}
						}
					}
				},
				"responses": {
					"204": {
						"description": "The operation was successful"
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			},
			"delete": {
				"summary": "Deletes a single object of the given entity",
				"tags": [
					"Generic entity interactions"
				],
				"parameters": [
					{
						"in": "path",
						"name": "entity",
						"required": true,
						"description": "A valid entity name",
						"schema": {
							"$ref": "#/components/schemas/ExposedEntity_NotIncludingNotDeletable"
						}
					},
					{
						"in": "path",
						"name": "objectId",
						"required": true,
						"description": "A valid object id of the given entity",
						"schema": {
							"type": "integer"
						}
					}
				],
				"responses": {
					"204": {
						"description": "The operation was successful"
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			}
		},
		"/userfields/{entity}/{objectId}": {
			"get": {
				"summary": "Returns all userfields with their values of the given object of the given entity",
				"tags": [
					"Generic entity interactions"
				],
				"parameters": [
					{
						"in": "path",
						"name": "entity",
						"required": true,
						"description": "A valid entity name",
						"schema": {
							"$ref": "#/components/schemas/ExposedEntity_IncludingUserEntities"
						}
					},
					{
						"in": "path",
						"name": "objectId",
						"required": true,
						"description": "A valid object id of the given entity",
						"schema": {
							"type": "integer"
						}
					}
				],
				"responses": {
					"200": {
						"description": "Key/value pairs of userfields",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"description": "Key/value pairs of userfields"
								}
							}
						}
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			},
			"put": {
				"summary": "Edits the given userfields of the given object of the given entity",
				"tags": [
					"Generic entity interactions"
				],
				"parameters": [
					{
						"in": "path",
						"name": "entity",
						"required": true,
						"description": "A valid entity name",
						"schema": {
							"$ref": "#/components/schemas/ExposedEntity_IncludingUserEntities_NotIncludingNotEditable"
						}
					},
					{
						"in": "path",
						"name": "objectId",
						"required": true,
						"description": "A valid object id of the given entity",
						"schema": {
							"type": "integer"
						}
					}
				],
				"requestBody": {
					"description": "A valid entity object of the entity specified in parameter *entity*",
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"description": "Key/value pairs of userfields"
							}
						}
					}
				},
				"responses": {
					"204": {
						"description": "The operation was successful"
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			}
		},
		"/files/{group}/{fileName}": {
			"get": {
				"summary": "Serves the given file",
				"description": "With proper Content-Type header",
				"tags": [
					"Files"
				],
				"parameters": [
					{
						"in": "path",
						"name": "group",
						"required": true,
						"description": "The file group",
						"schema": {
							"$ref": "#/components/schemas/FileGroups"
						}
					},
					{
						"in": "path",
						"name": "fileName",
						"required": true,
						"description": "The file name (including extension)<br>**BASE64 encoded**",
						"schema": {
							"type": "string"
						}
					},
					{
						"in": "query",
						"name": "force_serve_as",
						"required": false,
						"description": "Force the file to be served as the given type",
						"schema": {
							"type": "string",
							"enum": [
								"picture"
							]
						}
					},
					{
						"in": "query",
						"name": "best_fit_height",
						"required": false,
						"description": "Only when using `force_serve_as` = `picture`: Downscale the picture to the given height while maintaining the aspect ratio",
						"schema": {
							"type": "number"
						}
					},
					{
						"in": "query",
						"name": "best_fit_width",
						"required": false,
						"description": "Only when using `force_serve_as` = `picture`: Downscale the picture to the given width while maintaining the aspect ratio",
						"schema": {
							"type": "number"
						}
					}
				],
				"responses": {
					"200": {
						"description": "The binary file contents (Content-Type header is automatically set based on the file type)",
						"content": {
							"application/octet-stream": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							}
						}
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			},
			"put": {
				"summary": "Uploads a single file",
				"description": "The file will be stored at /data/storage/{group}/{file_name} (you need to remember the group and file name to get or delete it again)",
				"tags": [
					"Files"
				],
				"parameters": [
					{
						"in": "path",
						"name": "group",
						"required": true,
						"description": "The file group",
						"schema": {
							"$ref": "#/components/schemas/FileGroups"
						}
					},
					{
						"in": "path",
						"name": "fileName",
						"required": true,
						"description": "The file name (including extension)<br>**BASE64 encoded**",
						"schema": {
							"type": "string"
						}
					}
				],
				"requestBody": {
					"content": {
						"application/octet-stream": {
							"schema": {
								"type": "string",
								"format": "binary"
							}
						}
					}
				},
				"responses": {
					"204": {
						"description": "The operation was successful"
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			},
			"delete": {
				"summary": "Deletes the given file",
				"tags": [
					"Files"
				],
				"parameters": [
					{
						"in": "path",
						"name": "group",
						"required": true,
						"description": "The file group",
						"schema": {
							"$ref": "#/components/schemas/FileGroups"
						}
					},
					{
						"in": "path",
						"name": "fileName",
						"required": true,
						"description": "The file name (including extension)<br>**BASE64 encoded**",
						"schema": {
							"type": "string"
						}
					}
				],
				"responses": {
					"204": {
						"description": "The operation was successful"
					},
					"400": {
						"description": "The operation was not successful",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/Error400"
								}
							}
						}
					}
				}
			}
		},
		"/users": {
			"get": {
				"summary": "Returns all users",
				"tags": [
					"User management"
				],
				"parameters": [
					{
						"$ref": "#/components/parameters/query"
					},
					{
						"$ref": "#/components/parameters/order"
					},
					{
						"$ref": "#/components/parameters/limit"
					},
					{
						"$ref": "#/components/parameters/offset"
