      "AdminCreateUserRequest": {
        "type": "object",
        "required": ["UserPoolId", "Username"],
        "title": "AdminCreateUserRequest",
        "properties": {
          "UserPoolId": {
            "allOf": [
              { "$ref": "#/components/schemas/UserPoolIdType" },
              {
                "description": "The user pool ID for the user pool where the user will be created."
              }
