      "post": {
        "operationId": "apiTournamentPost",
        "summary": "Create a new Arena tournament",
        "description": "Create a public or private Arena tournament.\n\nThis endpoint mirrors the form on https://lichess.org/tournament/new.\n\nYou can create up to 12 public tournaments per day, or 24 private tournaments.\n",
        "tags": [
          "Arena tournaments"
        ],
        "security": [
          {
            "OAuth2": [
              "tournament:write"
            ]
          }
        ],
        "requestBody": {
          "description": "Parameters of the tournament",
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "name": {
                    "type": "string",
                    "description": "The tournament name. Leave empty to get a random Grandmaster name"
                  },
                  "clockTime": {
                    "type": "number",
                    "description": "Clock initial time in minutes",
                    "example": 2,
                    "minimum": 0,
                    "maximum": 60,
                    "enum": [
                      0,
                      0.25,
                      0.5,
                      0.75,
                      1,
                      1.5,
                      2,
                      3,
                      4,
                      5,
                      6,
                      7
                    ]
                  },
                  "clockIncrement": {
                    "type": "integer",
                    "description": "Clock increment in seconds",
                    "example": 1,
                    "minimum": 0,
                    "maximum": 60
                  },
                  "minutes": {
                    "type": "integer",
                    "description": "How long the tournament lasts, in minutes",
                    "example": 60,
                    "minimum": 0,
                    "maximum": 360
                  },
                  "waitMinutes": {
                    "type": "integer",
                    "description": "How long to wait before starting the tournament, from now, in minutes",
                    "minimum": 0,
                    "maximum": 360,
                    "default": 5
                  },
                  "startDate": {
                    "type": "integer",
                    "description": "Timestamp to start the tournament at a given date and time. Overrides the `waitMinutes` setting"
                  },
                  "variant": {
                    "type": "string",
                    "description": "The variant to use in tournament games",
                    "enum": [
                      "standard",
                      "chess960",
                      "crazyhouse",
                      "antichess",
                      "atomic",
                      "horde",
                      "kingOfTheHill",
                      "racingKings",
                      "threeCheck"
                    ],
                    "default": "standard"
                  },
                  "rated": {
