    "/api/swiss/new/{teamId}": {
      "post": {
        "operationId": "apiSwissNew",
        "summary": "Create a new Swiss tournament",
        "description": "Create a Swiss tournament for your team.\n\nThis endpoint mirrors the Swiss tournament form from your team pagee.\n\nYou can create up to 12 tournaments per day.\n",
        "tags": [
          "Swiss tournaments"
        ],
        "security": [
          {
            "OAuth2": [
              "tournament:write"
            ]
          }
        ],
        "parameters": [
          {
            "in": "path",
            "name": "teamId",
            "description": "ID of the team",
            "schema": {
              "type": "string"
            },
            "required": true
          }
        ],
        "requestBody": {
          "description": "Parameters of the tournament",
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "name": {
                    "type": "string",
                    "description": "The tournament name. Leave empty to get a random Grandmaster name"
                  },
                  "clock.limit": {
                    "type": "number",
                    "description": "Clock initial time in seconds",
                    "example": 300,
                    "minimum": 0,
                    "maximum": 3600
                  },
                  "clock.increment": {
                    "type": "integer",
                    "description": "Clock increment in seconds",
                    "example": 1,
                    "minimum": 0,
                    "maximum": 600
                  },
                  "nbRounds": {
                    "type": "integer",
                    "description": "Maximum number of rounds to play",
                    "minimum": 3,
                    "maximum": 100,
                    "default": 8
                  },
                  "startsAt": {
                    "type": "integer",
                    "description": "Timestamp in milliseconds to start the tournament at a given date and time. By default, it starts 10 minutes after creation."
                  },
                  "roundInterval": {
                    "type": "integer",
                    "description": "How long to wait between each round, in seconds.\n\nSet to 99999999 to manually schedule each round from the tournament UI.\n",
                    "minimum": 0,
                    "maximum": 86400,
                    "default": "auto"
                  },
                  "variant": {
                    "type": "string",
                    "description": "The variant to use in tournament games",
                    "enum": [
                      "standard",
                      "chess960",
                      "crazyhouse",
                      "antichess",
                      "atomic",
                      "horde",
                      "kingOfTheHill",
                      "racingKings",
                      "threeCheck"
                    ],
                    "default": "standard"
                  },
                  "description": {
                    "type": "string",
                    "description": "Anything you want to tell players about the tournament"
                  },
                  "rated": {
                    "type": "boolean",
                    "description": "Games are rated and impact players ratings",
                    "default": true
                  },
                  "chatFor": {
                    "type": "number",
                    "description": "Who can read and write in the chat.\n- 0  = No-one\n- 10 = Only team leaders\n- 20 = Only team members\n- 30 = All Lichess players\n",
                    "default": 20
                  }
                },
                "required": [
                  "clock.limit",
                  "clock.increment",
                  "nbRounds"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/SwissTournament"
                }
              }
            }
          },
          "400": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
