      "post": {
        "operationId": "broadcastCreate",
        "summary": "Create a broadcast",
        "description": "Create a new broadcast to relay external games.\nThis endpoint accepts the same form data as the [web form](https://lichess.org/broadcast/new).\n",
        "tags": [
          "Broadcasts",
          "OAuth"
        ],
        "security": [
          {
            "OAuth2": [
              "study:write"
            ]
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "name": {
                    "type": "string",
                    "description": "Name of the broadcast. Length must be between 3 and 80 characters.\n\nExample: `Sinquefield Cup - Round 10`\n"
                  },
                  "description": {
                    "type": "string",
                    "description": "Short description of the broadcast. Length must be between 3 and 400 characters.\n\nExample: `An 11 round classical tournament featuring the 9 highest rated players in the world. Including Carlsen, Caruana, Ding, Aronian, Nakamura and more.`\n"
                  },
                  "syncUrl": {
                    "type": "string",
                    "description": "URL that Lichess will poll to get updates about the games. It must be publicly accessible from the Internet.\n\nExample: `http://myserver.org/myevent/round-10/games.pgn`\n\nIf the syncUrl is missing, then the broadcast needs to be fed by [pushing PGN to it](#operation/broadcastPush).\n"
                  },
                  "markdown": {
                    "type": "string",
                    "description": "Optional long description of the broadcast. Markdown is supported. Length must be less than 20,000 characters."
                  },
                  "credit": {
                    "type": "string",
                    "description": "Optional short text to give credit to the source provider."
                  },
                  "startsAt": {
                    "type": "integer",
                    "description": "Timestamp in milliseconds of broadcast start. Leave empty to manually start the broadcast.\n\nExample: `1356998400070`\n",
                    "schema": {
                      "type": "integer",
                      "minimum": 1356998400070
                    }
                  },
                  "official": {
                    "type": "boolean",
                    "description": "For Lichess internal usage only. You are not allowed to use this flag. If you do it, we will have to call the police."
                  },
                  "throttle": {
                    "type": "integer",
                    "description": "For Lichess internal usage only. You are not allowed to use this flag. If you do it, chess24 will [display ads on your broadcast](https://i.imgur.com/B47wT5P.jpg)."
                  }
                },
                "required": [
                  "name",
                  "description"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Broadcast"
                }
              }
            }
          },
          "400": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
