    "/api/challenge/{username}": {
      "post": {
        "operationId": "challengeCreate",
        "summary": "Create a challenge",
        "description": "Challenge someone to play. The targeted player can choose to accept or decline.\n\nIf the challenge is accepted, you will be notified on the [event stream](#operation/apiStreamEvent)\nthat a new game has started. The game ID will be the same as the challenge ID.\n\nIf you also have an OAuth token with `challenge:write` scope for the receiving user,\nyou can make them accept the challenge immediately by setting the `acceptByToken` field.\n",
        "tags": [
          "Challenges"
        ],
        "security": [
          {
            "OAuth2": [
              "challenge:write",
              "bot:play",
              "board:play"
            ]
          }
        ],
        "parameters": [
          {
            "in": "path",
            "name": "username",
            "schema": {
              "type": "string",
              "example": "LeelaChess"
            },
            "required": true
          }
        ],
        "requestBody": {
          "description": "Parameters of the challenge",
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "rated": {
                    "type": "boolean",
                    "description": "Game is rated and impacts players ratings"
                  },
                  "clock.limit": {
                    "type": "number",
                    "description": "Clock initial time in seconds. If empty, a correspondence game is created.",
                    "example": 300,
                    "minimum": 0,
                    "maximum": 10800
                  },
                  "clock.increment": {
                    "type": "integer",
                    "description": "Clock increment in seconds. If empty, a correspondence game is created.",
                    "example": 1,
                    "minimum": 0,
                    "maximum": 60
                  },
                  "days": {
                    "type": "integer",
                    "description": "Days per move, for correspondence games. Clock settings must be omitted.",
                    "example": 5,
                    "minimum": 1,
                    "maximum": 15
                  },
                  "color": {
                    "type": "string",
                    "description": "Which color you get to play",
                    "enum": [
                      "random",
                      "white",
                      "black"
                    ],
                    "default": "random"
                  },
                  "variant": {
                    "type": "string",
                    "description": "The variant of the game",
                    "enum": [
                      "standard",
                      "chess960",
                      "crazyhouse",
                      "antichess",
                      "atomic",
                      "horde",
                      "kingOfTheHill",
                      "racingKings",
                      "threeCheck"
                    ],
                    "default": "standard"
                  },
                  "fen": {
                    "type": "string",
                    "description": "Custom initial position (in FEN). Variant must be standard, and the game cannot be rated. Castling moves will use UCI_Chess960 notation, for example e1h1 instead of e1g1.",
                    "default": "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1"
                  },
                  "acceptByToken": {
                    "type": "string",
                    "description": "Immediately accept the challenge and create the game.\nPass in an OAuth token (with the `challenge:write` scope) for the receiving user.\nOn success, the response will contain a `game` field instead of a `challenge` field.\n"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ChallengeJson"
                }
              }
            }
          },
          "400": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
