components:
  schemas:
    tmp0:
      properties:
        json: {type: string}
      type: object
    tmp1:
      properties:
        computer_name: {type: string}
        domain_name: {type: string}
        ipv4_address: {type: string}
        ipv6_address: {type: string}
        mac_address: {type: string}
        name: {type: string}
        nat_ip_address: {type: string}
        open_ports: {type: string}
        process: {type: string}
        target_ip: {type: string}
      type: object
    tmp2:
      properties:
        computer_name: {type: string}
        installed_pkgs: {type: string}
        installed_rpms: {type: string}
        login_hooks: {type: string}
        logout_hooks: {type: string}
        name: {type: string}
        process_name: {type: string}
        service: {type: string}
        stopped_service: {type: string}
        version: {type: string}
      type: object
    tmp3:
      properties:
        arch: {type: string}
        bios_name: {type: string}
        bios_vendor: {type: string}
        bios_version: {type: string}
        computer_name: {type: string}
        cpu: {type: string}
        free_memory: {type: string}
        model: {type: string}
        operating_system: {type: string}
        operating_system_install_date: {type: string}
      type: object
  securitySchemes:
    BasicAuth: {scheme: basic, type: http}
info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Tanium Tanium
  title: Tanium Tanium
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Asks a question from Tanium
      parameters:
      - description: Question to ask Tanium. See https://docs.tanium.com/interact/interact/questions.html
        example: Get Computer Name from all machines
        in: query
        name: Question
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Tanium Generic Question
  tmp1:
    post:
      description: Gets networking information about the machines on Tanium
      parameters:
      - description: Filter which machines are included in results, defaults to all
          machines. See https://kb.tanium.com/Asking_Questions
        example: all machines | all machines with IP Address containing "10.8.65"
        in: query
        name: Filter
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Tanium Network Information
  tmp2:
    post:
      description: Gets software information about the machines on Tanium
      parameters:
      - description: Filter which machines are included in results, defaults to all
          machines. See https://kb.tanium.com/Asking_Questions
        example: all machines | all machines with IP Address containing "10.8.65"
        in: query
        name: Filter
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Tanium Software Information
  tmp3:
    post:
      description: Gets system information about the machines on Tanium
      parameters:
      - description: Filter which machines are included in results, defaults to all
          machines. See https://kb.tanium.com/Asking_Questions
        example: all machines | all machines with IP Address containing "10.8.65"
        in: query
        name: Filter
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Tanium System Information
tags:
- {name: Endpoint Security & Management}
