      "k8s.io.api.core.v1.NodeAffinity": {
        "description": "Node affinity is a group of node affinity scheduling rules.",
        "type": "object",
        "properties": {
          "requiredDuringSchedulingIgnoredDuringExecution": {
            "$ref": "#/components/schemas/k8s.io.api.core.v1.NodeSelector"
          },
          "preferredDuringSchedulingIgnoredDuringExecution": {
            "description": "The scheduler will prefer to schedule pods to nodes that satisfy the affinity expressions specified by this field, but it may choose a node that violates one or more of the expressions. The node that is most preferred is the one with the greatest sum of weights, i.e. for each node that meets all of the scheduling requirements (resource request, requiredDuringScheduling affinity expressions, etc.), compute a sum by iterating through the elements of this field and adding \"weight\" to the sum if the node matches the corresponding matchExpressions; the node(s) with the highest sum are the most preferred.",
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/k8s.io.api.core.v1.PreferredSchedulingTerm"
            }
          }
        }
      },
      "k8s.io.api.core.v1.NodeSelector": {
        "description": "A node selector represents the union of the results of one or more label queries over a set of nodes; that is, it represents the OR of the selectors represented by the node selector terms.",
        "type": "object",
        "properties": {
          "nodeSelectorTerms": {
            "description": "Required. A list of node selector terms. The terms are ORed.",
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/k8s.io.api.core.v1.NodeSelectorTerm"
            }
          }
        }
      },
      "k8s.io.api.core.v1.NodeSelectorRequirement": {
        "description": "A node selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
        "type": "object",
        "properties": {
          "key": {
            "description": "The label key that the selector applies to.",
            "type": "string"
          },
          "operator": {
            "description": "Represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists, DoesNotExist. Gt, and Lt.",
            "type": "string"
          },
          "values": {
            "description": "An array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. If the operator is Gt or Lt, the values array must have a single element, which will be interpreted as an integer. This array is replaced during a strategic merge patch.",
            "type": "array",
            "items": {
              "type": "string"
            }
          }
        }
      },
      "k8s.io.api.core.v1.NodeSelectorTerm": {
        "description": "A null or empty node selector term matches no objects. The requirements of them are ANDed. The TopologySelectorTerm type implements a subset of the NodeSelectorTerm.",
        "type": "object",
        "properties": {
          "matchExpressions": {
            "description": "A list of node selector requirements by node's labels.",
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/k8s.io.api.core.v1.NodeSelectorRequirement"
            }
          },
          "matchFields": {
            "description": "A list of node selector requirements by node's fields.",
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/k8s.io.api.core.v1.NodeSelectorRequirement"
            }
          }
        }
      },
      "k8s.io.api.core.v1.ObjectFieldSelector": {
        "description": "ObjectFieldSelector selects an APIVersioned field of an object.",
        "type": "object",
        "properties": {
          "apiVersion": {
            "description": "Version of the schema the FieldPath is written in terms of, defaults to \"v1\".",
            "type": "string"
          },
          "fieldPath": {
            "description": "Path of the field to select in the specified API version.",
            "type": "string"
          }
        }
      },
      "k8s.io.api.core.v1.PersistentVolumeClaimVolumeSource": {
        "description": "PersistentVolumeClaimVolumeSource references the user's PVC in the same namespace. This volume finds the bound PV and mounts that volume for the pod. A PersistentVolumeClaimVolumeSource is, essentially, a wrapper around another type of volume that is owned by someone else (the system).",
        "type": "object",
        "properties": {
          "readOnly": {
            "description": "Will force the ReadOnly setting in VolumeMounts. Default false.",
            "type": "boolean"
          },
          "claimName": {
            "description": "ClaimName is the name of a PersistentVolumeClaim in the same namespace as the pod using this volume. More info: https://kubernetes.io/docs/concepts/storage/persistent-volumes#persistentvolumeclaims",
            "type": "string"
          }
        }
      },
      "k8s.io.api.core.v1.PhotonPersistentDiskVolumeSource": {
        "description": "Represents a Photon Controller persistent disk resource.",
        "type": "object",
        "properties": {
          "fsType": {
            "description": "Filesystem type to mount. Must be a filesystem type supported by the host operating system. Ex. \"ext4\", \"xfs\", \"ntfs\". Implicitly inferred to be \"ext4\" if unspecified.",
