                      ],
                      "example": 400
                    },
                    "message": {
                      "type": "string",
                      "enum": [
                        "missing 'selection' parameter",
                        "invalid 'selection' parameter (accepted 'lat;long', 'lat1;long1;lat2;long2', 'wkt:...' or 'geo:...' )"
                      ],
                      "example": "missing 'selection' parameter\n"
                    },
                    "apiDoc": {
                      "type": "string",
                      "format": "uri",
                      "enum": [
                        "http://www.disit.org/6991"
