          {
            "in": "query",
            "name": "routeType",
            "description": "Can be _public_transport_, _foot_shortest_, _foot_quiet_, or _car_. It defaults to _foot_shortest_.\n\n_Example:_ foot_shortest\n",
            "schema": {
              "type": "string",
              "enum": [
                "public_transport",
                "foot_shortest",
                "foot_quiet",
                "car"
              ],
              "default": "foot_shortest"
            }
          },
          {
            "in": "query",
            "name": "startDatetime",
