            "in": "query",
            "name": "per_page",
            "schema": {
              "default": 100,
              "format": "int64",
              "maximum": 500,
              "minimum": 1,
              "type": "integer"
            }
          },
          {
            "description": "Pagination Page",
            "in": "query",
            "name": "page",
            "schema": {
              "default": 0,
              "format": "int64",
