      "post": {
        "Summary": "create an auth token. The token has one month valition length.",
        "RequestBody": {
          "Content": {
            "application/json": {
              "Schema": {
                "Required": [
                  "apikey"
                ],
                "Properties": {
                  "apikey": {
                    "Type": "string"
                  },
                  "pin": {
                    "Type": "string"
                  }
                },
                "Type": "object"
              }
            }
          },
          "Required": true
        },
        "Responses": {
          "200": {
            "Description": "response",
            "Content": {
              "application/json": {
                "Schema": {
                  "Properties": {
                    "data": {
                      "Properties": {
                        "token": {
                          "Type": "string"
                        }
                      },
                      "Type": "object"
                    },
                    "status": {
                      "Type": "string"
                    }
                  },
                  "Type": "object"
                }
              }
            }
          },
          "401": {
            "Description": "invalid credentials"
          }
        },
        "Tags": [
          "Login"
        ]
      }
    },
