{
  "openapi": "3.0.0",
  "info": {
    "description": "This is the API specication for the [Wazigte Edge](https://github.com/Waziup/wazigate-edge).",
    "version": "1.0.0",
    "title": "Wazigate Edge",
    "termsOfService": "http://swagger.io/terms/",
    "contact": {
      "email": "johann.forster@waziup.org"
    },
    "license": {
      "name": "Apache 2.0",
      "url": "http://www.apache.org/licenses/LICENSE-2.0.html"
    }
  },
  "tags": [
    {
      "name": "Auth",
      "description": "Authentication & Authorization"
    },
    {
      "name": "Your Gateway",
      "description": "Local Device Management"
    },
    {
      "name": "Devices",
      "description": "Device Management"
    },
    {
      "name": "Sensors",
      "description": "Sensor Management and Sensor Values"
    },
    {
      "name": "Actuators",
      "description": "Actuator Management and Actuator Values"
    },
    {
      "name": "Clouds",
      "description": "Clouds and Synchronization"
    },
    {
      "name": "System",
      "description": "System Information"
    }
  ],
  "paths": {
    "/auth/token": {
      "post": {
        "tags": [
          "Auth"
        ],
        "summary": "Authentication",
        "description": "Login and get a token.",
        "requestBody": {
          "description": "Credentials",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/AuthBody"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Token"
                }
              }
            }
          }
        }
      }
    },
    "/auth/retoken": {
      "post": {
        "tags": [
          "Auth"
        ],
        "summary": "Referesh the authorized token",
        "description": "Pass the current valid token and get a fresh token",
        "requestBody": {
          "description": "Current valid token",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Token"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Token"
                }
              }
            }
          }
        }
      }
    },
    "/auth/profile": {
      "get": {
        "tags": [
          "Auth"
        ],
        "summary": "Authorized user profile",
        "description": "Retrieves the user data via a given valid token",
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UserProfile"
                }
              }
            }
          }
        }
      }
    },
    "/device/id": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Your Gateway's ID",
        "description": "Return the ID of this Gateway. It is generated from the physical MAC Address and is unique for each device.",
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/ID"
                }
              }
            }
          }
        }
      }
    },
    "/device": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Your Gateway",
        "description": "Return the local device (gateway).",
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Device"
                }
              }
            }
          }
        }
      }
    },
    "/device/name": {
      "post": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Change Name",
        "description": "Change the name of your gateway.",
        "requestBody": {
          "description": "New Name",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "string",
                "example": "My New Gateay Name"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Device"
                }
              }
            }
          }
        }
      }
    },
    "/sensors": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "List Gateway Sensors",
        "description": "Return a list of sensors for this device.",
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Sensor"
                  },
                  "example": [
                    {
                      "id": "5da45ed64b9f614d30e93ab3",
                      "name": "Tempature Sensor",
                      "created": "2019-10-14T11:41:10.814Z",
                      "time": "2019-10-14T11:41:10.811Z",
                      "value": 40.21
                    },
                    {
                      "id": "5da97e3813474778618e2289",
                      "name": "Water Level Sensor",
                      "created": "2019-10-18T08:56:59.635Z",
                      "time": "2019-10-18T08:57:12.250Z",
                      "value": 0.52
                    }
                  ]
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Declare a Sensor",
        "description": "Create a new sensor on this device.",
        "requestBody": {
          "description": "Sensor to be declared. The Sensor ID is not required - the Edge API will generate an ID for you.",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "description": "Sensor ID",
                    "example": "201c85cdbda3"
                  },
                  "name": {
                    "type": "string",
                    "description": "Sensor Name",
                    "example": "Temperature Sensor"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Sensor created. The Sensor ID will be returned.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ID"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request. The sensor could not be created, maybe because a sensor with that ID already exists."
          }
        }
      }
    },
    "/sensors/{id}": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Get a single Sensor",
        "description": "",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the sensor",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Sensor"
                }
              }
            }
          },
          "404": {
            "description": "No sensor with that ID found."
          }
        }
      },
      "delete": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Delete a single Sensor",
        "description": "",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the sensor",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Operation"
          },
          "404": {
            "description": "No sensor with that ID found."
          }
        }
      }
    },
    "/sensors/{id}/value": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Last Sensor Value",
        "description": "Get the last sensor value.",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the sensor",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "description": "Can be any value - string, number, boolean, array or object.",
                  "example": 45.2
                }
              }
            }
          },
          "404": {
            "description": "No sensor with that ID found."
          }
        }
      },
      "post": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Set a Sensor Value",
        "description": "Upload a new value for this sensor.",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the sensor",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "description": "The new sensor value. You can upload either a **value+time** or only a **value** (then the API uses the server time).",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "oneOf": [
                  {
                    "description": "Can be any value - string, number, boolean, array or object.",
                    "example": 7.25
                  },
                  {
                    "type": "object",
                    "properties": {
                      "value": {
                        "description": "Can be any value - string, number, boolean, array or object.",
                        "example": 7.25
                      },
                      "time": {
                        "type": "string",
                        "description": "Value time",
                        "format": "date-time",
                        "example": "2019-10-18T08:57:12.250Z"
                      }
                    }
                  }
                ]
              },
              "examples": {
                "single": {
                  "value": 7.2,
                  "summary": "Single value (without time)"
                },
                "time": {
                  "value": {
                    "value": 7.25,
                    "time": "2019-10-18T08:57:12.250Z"
                  },
                  "summary": "Value and Time"
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Sensor value created."
          },
          "400": {
            "description": "Bad Request. There may be no sensor with that id."
          }
        }
      }
    },
    "/sensors/{id}/values": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Sensor Values",
        "description": "List sensor values.",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the sensor",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "from",
            "description": "Only values newer (not including) than this time.",
            "schema": {
              "type": "string",
              "format": "date-time"
            }
          },
          {
            "in": "query",
            "name": "to",
            "description": "Only values older (including) than this time.",
            "schema": {
              "type": "string",
              "format": "date-time"
            }
          },
          {
            "in": "query",
            "name": "limt",
            "description": "Maximum number of values to return.",
            "schema": {
              "type": "number"
            }
          },
          {
            "in": "query",
            "name": "size",
            "description": "Maximum memory size of the response.",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "time": {
                        "type": "string",
                        "format": "date-time",
                        "description": "Time this value was recieved."
                      },
                      "value": {
                        "description": "Can be any value - string, number, boolean, array or object."
                      }
                    }
                  },
                  "example": [
                    {
                      "time": "2019-05-13T13:00:00",
                      "value": 7
                    },
                    {
                      "time": "2019-05-13T13:01:00",
                      "value": 8
                    },
                    {
                      "time": "2019-05-13T13:02:00",
                      "value": 7.5
                    },
                    {
                      "time": "2019-05-13T13:03:00",
                      "value": 7
                    }
                  ]
                }
              }
            }
          },
          "404": {
            "description": "No sensor with that ID found."
          }
        }
      },
      "post": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Upload multiple Values",
        "description": "Upload multiple new values for this sensor.",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the sensor",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "description": "The new sensor values. You can upload either a list of **values+times** or only **values** (then the API uses the server time).",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "oneOf": [
                  {
                    "type": "array",
                    "items": {
                      "description": "Can be any value - string, number, boolean, array or object.",
                      "example": 7.25
                    }
                  },
                  {
                    "type": "array",
                    "items": {
                      "type": "object",
                      "properties": {
                        "value": {
                          "description": "Can be any value - string, number, boolean, array or object.",
                          "example": 7.25
                        },
                        "time": {
                          "type": "string",
                          "description": "Value time",
                          "format": "date-time",
                          "example": "2019-10-18T08:57:12.250Z"
                        }
                      }
                    }
                  }
                ]
              },
              "examples": {
                "single": {
                  "value": [
                    7.2,
                    7.9,
                    8.1
                  ],
                  "summary": "Values only"
                },
                "time": {
                  "value": [
                    {
                      "value": 7.25,
                      "time": "2019-10-18T08:57:00.000Z"
                    },
                    {
                      "value": 7.9,
                      "time": "2019-10-18T08:58:00.000Z"
                    },
                    {
                      "value": 8.1,
                      "time": "2019-10-18T08:59:00.000Z"
                    }
                  ],
                  "summary": "Value and Time"
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Sensor values created."
          },
          "400": {
            "description": "Bad Request. There may be no sensor with that id."
          }
        }
      }
    },
    "/sensors/{id}/meta": {
      "post": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Sensor Metadata",
        "description": "Change this sensors metadata. Metadata can be any object.",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the sensor",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "description": "New metadata. Metadata can be any object, so feel free to add new fields to the metadata object.",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "additionalProperties": true,
                "properties": {
                  "syncInterval": {
                    "type": "string",
                    "example": "5m30s"
                  },
                  "doNotSync": {
                    "type": "boolean",
                    "example": false
                  }
                }
              },
              "examples": {
                "default": {
                  "value": {
                    "syncInterval": "5m",
                    "doNotSync": false
                  },
                  "summary": "Default (5 min sync)"
                },
                "oneday": {
                  "value": {
                    "syncInterval": "24h"
                  },
                  "summary": "Sync once a day"
                },
                "donotsync": {
                  "value": {
                    "doNotSync": true
                  },
                  "summary": "Do not sync"
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Metadata updated."
          },
          "400": {
            "description": "Bad Request. There may be no sensor with that id."
          }
        }
      }
    },
    "/actuators": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "List Gateway Actuators",
        "description": "Return a list of actuators for this device.",
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Actuator"
                  },
                  "example": [
                    {
                      "id": "5da45ed64b9f614d30e93ab3",
                      "name": "Volt Level",
                      "created": "2019-10-14T11:41:10.814Z",
                      "time": "2019-10-14T11:41:10.811Z",
                      "value": 5
                    },
                    {
                      "id": "5da97e3813474778618e2289",
                      "name": "LCD Output",
                      "created": "2019-10-18T08:56:59.635Z",
                      "time": "2019-10-18T08:57:12.250Z",
                      "value": "Hello World"
                    }
                  ]
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Declare an Actuator",
        "description": "Create a new actuator on this device.",
        "requestBody": {
          "description": "Actuator to be declared. The Actuator ID is not required - the Edge API will generate an ID for you.",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "description": "Actuator ID",
                    "example": "201c85cdbda3"
                  },
                  "name": {
                    "type": "string",
                    "description": "Actuator Name",
                    "example": "Volt Level"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Actuator created. The Actuator ID will be returned.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ID"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request. The actuator could not be created, maybe because a actuator with that ID already exists."
          }
        }
      }
    },
    "/actuators/{id}": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Get a single Actuator",
        "description": "",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the actuator",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Actuator"
                }
              }
            }
          },
          "404": {
            "description": "No actuator with that ID found."
          }
        }
      },
      "delete": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Delete a single Actuator",
        "description": "",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the actuator",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Operation"
          },
          "404": {
            "description": "No actuator with that ID found."
          }
        }
      }
    },
    "/actuators/{id}/value": {
      "get": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Actuator Value",
        "description": "Get the actuator value.",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the actuator",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "description": "Can be any value - string, number, boolean, array or object.",
                  "example": 45.2
                }
              }
            }
          },
          "404": {
            "description": "No atuator with that ID found."
          }
        }
      },
      "post": {
        "tags": [
          "Your Gateway"
        ],
        "summary": "Set an Actuator Value",
        "description": "Change the value of this actuator.",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "ID of the actuator",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "description": "The new actuator value.",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "oneOf": [
                  {
                    "type": "string"
                  },
                  {
                    "type": "object",
                    "properties": {
                      "value": {
                        "description": "Can be any value - string, number, boolean, array or object.",
                        "example": 45.2
                      },
                      "time": {
                        "type": "string",
                        "description": "Value time",
                        "format": "date-time",
                        "example": "2019-10-18T08:57:12.250Z"
                      }
                    }
                  }
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Actuator value created."
          },
          "400": {
            "description": "Bad Request. The actuator could not be created, maybe because a actuator with that ID already exists."
          }
        }
      }
    },
    "/devices": {
      "get": {
        "tags": [
          "Devices"
        ],
        "summary": "List all Devices",
        "description": "Returns a list of all devices.",
        "responses": {
          "200": {
            "description": "Successful Operation",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Device"
                  }
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "Devices"
        ],
        "summary": "Declare a new Device",
        "description": "Add a new deivce to the list of devices.",
        "requestBody": {
          "description": "Device to be declared. Device and Sensor/Actuator IDs are not required - the Edge API will generate an ID for you.",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "description": "Gateway ID",
                    "example": "201c85cdbda3"
                  },
                  "name": {
                    "type": "string",
                    "description": "Gateway Name",
                    "example": "My New Gateway"
                  },
                  "sensors": {
                    "type": "array",
                    "items": {
                      "type": "object",
                      "properties": {
                        "id": {
                          "type": "string",
                          "description": "Sensor ID",
                          "example": "201c85cdbda3"
                        },
                        "name": {
                          "type": "string",
                          "description": "Sensor Name",
                          "example": "Temperature Sensor"
                        }
                      }
                    },
                    "example": [
                      {
