	"paths": {
		"/delivery/pools": {
			"get": {
				"tags": [
					"delivery-pools-api"
				],
				"summary": "Get your delivery pools",
				"description": "A delivery pool is a named group of drivers which deliver food for a set of restaurants.",
				"responses": {
					"200": {
						"description": "OK",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"properties": {
										"delivery-pool-id": {
											"type": "object",
											"properties": {
												"name": {
													"type": "string",
													"description": "The name of the pool, used by operations teams, in reports, etc."
												},
												"restaurants": {
													"description": "An array of JUST EAT restaurant ids served by the pool.",
													"type": "array",
													"items": {
														"type": "number",
														"format": "int32"
													}
												}
											}
										}
									}
								},
								"examples": {
									"response": {
										"value": {
											"d5f72466a6dd49a08166d5a044c5b9e4": {
												"name": "Toronto - West",
												"restaurants": [
													123,
													456
												]
											},
											"b9c6673b8e5948b98cfbd14a982de2c2": {
												"name": "Toronto - East",
												"restaurants": [
													789
												]
											}
										}
									}
								}
							}
						}
					}
				}
			},
			"post": {
				"tags": [
					"delivery-pools-api"
				],
				"summary": "Create a new delivery pool",
				"description": "A delivery pool is a named group of drivers which deliver food for a set of restaurants.",
				"requestBody": {
					"required": true,
					"content": {
						"application/json": {
							"schema": {
								"type": "object",
								"properties": {
									"name": {
										"type": "string",
										"description": "The name of the pool, used by operations teams, in reports, etc."
									},
									"restaurants": {
										"description": "An array of JUST EAT restaurant ids served by the pool.",
										"type": "array",
										"items": {
											"type": "number",
											"format": "int32"
										}
									}
								}
							}
						}
					}
				},
				"responses": {
					"201": {
						"description": "Pool has been created."
					},
					"400": {
						"description": "There was something invalid in your request - check response body for details"
					}
				}
			}
		},
		"/delivery/pools/{deliveryPoolId}": {
			"parameters": [
				{
					"name": "deliveryPoolId",
					"in": "path",
					"required": true,
					"description": "Identifier for the pool",
					"schema": {
						"type": "string"
					}
				}
			],
