														"startTime": {
															"type": "string",
															"description": "pool start time for Monday (HH:mm)"
														},
														"endTime": {
															"type": "string",
															"description": "pool end time for Monday (HH:mm)"
														}
													},
													"required": [
														"startTime",
														"endTime"
													]
												}
											}
										},
										"required": [
											"poolTimes"
										]
									},
									"tuesday": {
										"type": "object",
										"properties": {
											"closed": {
												"type": "boolean",
												"description": "true if the pool is closed for the day"
											},
											"poolTimes": {
												"type": "array",
												"description": "pool start and end times for the day",
												"items": {
													"type": "object",
													"properties": {
