															"type": "string",
															"description": "pool start time for Tuesday (HH:mm)"
														},
														"endTime": {
															"type": "string",
															"description": "pool end time for Tuesday (HH:mm)"
														}
													},
													"required": [
														"startTime",
														"endTime"
													]
												}
											}
										},
										"required": [
											"poolTimes"
										]
									},
									"wednesday": {
										"type": "object",
										"properties": {
											"closed": {
												"type": "boolean",
												"description": "true if the pool is closed for the day"
											},
											"poolTimes": {
												"type": "array",
												"description": "pool start and end times for the day",
												"items": {
													"type": "object",
													"properties": {
														"startTime": {
															"type": "string",
															"description": "pool start time for Wednesday (HH:mm)"
														},
														"endTime": {
															"type": "string",
															"description": "pool end time for Wednesday (HH:mm)"
														}
													},
													"required": [
														"startTime",
														"endTime"
													]
												}
											}
										},
										"required": [
											"poolTimes"
										]
									},
									"thursday": {
										"type": "object",
										"properties": {
											"closed": {
												"type": "boolean",
												"description": "true if the pool is closed for the day"
											},
											"poolTimes": {
												"type": "array",
												"description": "pool start and end times for the day",
												"items": {
													"type": "object",
													"properties": {
