{
  "openapi": "3.0.3",
  "components": {
    "schemas": {
      "vulnerability": {
        "description": "Represents a common vulnerability and exposure (CVE).",
        "properties": {
          "CVSS2Score": {
            "description": "Common Vulnerability Scoring System (CVSS) version 2 score.",
            "example": 3.2,
            "type": "number"
          },
          "ID": {
            "description": "Identifier of the object.",
            "readOnly": true,
            "type": "string"
          },
          "annotations": {
            "additionalProperties": {
              "items": {
                "type": "string"
              },
              "type": "array"
            },
            "description": "Stores additional information about an entity.",
            "type": "object"
          },
          "associatedTags": {
            "description": "List of tags attached to an entity.",
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "description": {
            "description": "Description of the object.",
            "type": "string"
          },
          "link": {
            "description": "The URL that refers to the vulnerability.",
            "example": "https://cve.com/CVE-1234",
            "type": "string"
          },
          "name": {
            "description": "Name of the entity.",
            "example": "the name",
            "type": "string"
          },
          "namespace": {
            "description": "Namespace tag attached to an entity.",
            "readOnly": true,
            "type": "string"
          },
          "normalizedTags": {
            "description": "Contains the list of normalized tags of the entities.",
            "items": {
              "type": "string"
            },
            "readOnly": true,
            "type": "array"
          },
          "propagate": {
            "description": "Propagates the policy to all of its children.",
            "type": "boolean"
          },
          "protected": {
            "description": "Defines if the object is protected.",
            "type": "boolean"
          },
          "severity": {
            "description": "Refers to the security vulnerability level.",
            "example": 3,
            "maximum": 7,
            "minimum": 0,
            "type": "integer"
          }
        },
        "required": [
          "link",
          "name",
          "severity"
        ],
        "type": "object"
      }
    }
  },
  "info": {
    "contact": {
      "email": "dev@aporeto.com",
      "name": "Aporeto Inc.",
      "url": "go.aporeto.io/api"
    },
    "license": {
      "name": "TODO"
    },
    "termsOfService": "https://localhost/TODO",
    "title": "vulnerability",
    "version": "1.0"
  },
  "paths": {
    "/vulnerabilities": {
      "get": {
        "description": "Retrieves the list of vulnerabilities.",
        "operationId": "get-all-vulnerabilities",
        "parameters": [
          {
            "description": "Also retrieve the objects that propagate down.",
            "in": "query",
            "name": "propagated",
            "schema": {
              "type": "boolean"
            }
          },
          {
            "description": "Filtering query. Consequent `q` parameters will form an or.",
            "example": "associatedTags contains ['$name=hello'] or type == 'Docker'",
            "in": "query",
            "name": "q",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "items": {
                    "$ref": "#/components/schemas/vulnerability"
                  },
                  "type": "array"
                }
              }
            },
            "description": "n/a"
          }
        },
        "tags": [
          "core/processingunit",
          "squall"
        ]
      },
      "post": {
        "description": "Creates a new vulnerability.",
        "operationId": "create-a-new-vulnerability",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/vulnerability"
              }
            }
          }
        },
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/vulnerability"
                }
              }
            },
            "description": "n/a"
          }
        },
        "tags": [
          "core/processingunit",
          "squall"
        ]
      }
    },
    "/vulnerabilities/{id}": {
      "get": {
        "description": "Retrieves the object with the given ID.",
        "operationId": "get-vulnerability-by-ID",
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/vulnerability"
                }
              }
            },
            "description": "n/a"
          }
        },
        "tags": [
          "core/processingunit",
          "squall"
        ]
      },
      "parameters": [
        {
          "in": "path",
          "name": "id",
          "required": true,
          "schema": {
            "type": "string"
          }
        }
      ]
    },
    "/vulnerabilities/{id}/processingunits": {
      "get": {
        "description": "Retrieves the processing units affected by the vulnerability.",
        "operationId": "get-all-processingunits-for-a-given-vulnerability",
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "items": {
                    "$ref": "./processingunit#/components/schemas/processingunit"
                  },
                  "type": "array"
                }
              }
            },
            "description": "n/a"
          }
        },
        "tags": [
          "core/processingunit",
          "squall"
        ]
      },
      "parameters": [
        {
          "in": "path",
          "name": "id",
          "required": true,
          "schema": {
            "type": "string"
          }
        }
      ]
    }
  },
  "tags": [
    {
      "description": "This tag is for group 'core/processingunit'",
      "name": "core/processingunit"
    },
    {
      "description": "This tag is for package 'squall'",
      "name": "squall"
    }
  ]
}
