                    "type": "array",
                    "items": { "$ref": "#/components/schemas/Tag" }
                  },
                  "DkimSigningAttributes": {
                    "description": "An object that contains information about the tokens used for setting up Bring Your Own DKIM (BYODKIM).",
                    "type": "object",
                    "properties": {
                      "DomainSigningSelector": {
                        "allOf": [
