                          {
                            "description": "A string that's used to identify a public key in the DNS configuration for a domain."
                          }
                        ]
                      },
                      "DomainSigningPrivateKey": {
                        "allOf": [
                          { "$ref": "#/components/schemas/PrivateKey" },
