          {
            "name": "id",
            "in": "path",
            "required": true,
            "description": "Purchase Id",
            "schema": {
              "type": "integer",
              "format": "int32"
            },
            "x-position": 1
          }
        ],
        "responses": {
          "200": {
            "description": "Successful request",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/SinglePurchaseResponse"
                }
              }
            }
          },
          "401": {
            "description": "Invalid credentials"
