paths:
  tmp0:
    post:
      description: Vectra Cognito Create a new triage rule for Vectra
      parameters:
      - description: User defined description for the rule
        example: ''
        in: query
        name: Description
        required: true
        schema: {type: string}
      - description: The detection that must be triaged. Use the detection name as
          seen in the UI or the 'Understanding Vectra Detections' guide
        example: ''
        in: query
        name: Detection
        required: true
        schema: {type: string}
      - description: ' Must be set to the category of the detection LATERAL MOVEMENT,
          RECONNAISSANCE, COMMAND & CONTROL, EXFILTRATION, BOTNET, INFO'
        example: ''
        in: query
        name: Detection Category
        required: true
        schema: {type: string}
      - description: The name that will be used for the triaged detection. Only applies
          if is_whitelist is set to false
        example: ''
        in: query
        name: Triage Category
        required: true
        schema: {type: string}
      - description: A Boolean flag indicating whether to create a 'whitelist' or
          a 'track without scores' rule
        example: ''
        in: query
        name: Whitelist
        required: false
        schema: {type: boolean}
      - description: JSON String; will overwrite conditions
        example: '{''OR'': [{''AND'': [{''ANY_OF'': {''field'': ''ip'', ''values'':
          [{''value'': ''1.1.1.1''}, {''value'': ''1.2.1.3''}], ''groups'': [{''value'':
          13}]}}]}]}'
        in: query
        name: Custom Source Conditions
        required: false
        schema: {type: string}
      - description: 'Must be one of the following: ip, host, sensor, account.'
        example: ip
        in: query
        name: Source Field
        required: false
        schema: {type: string}
      - description: Value or CSV of multiple values
        example: 1.1.1.1, 2.2.2.2
        in: query
        name: Source Values
        required: false
        schema: {type: string}
      - description: Field group ID, or CSV of IDs, for applying triage rule to entire
          group. Only fields ip and host support triaging on groups.
        example: 13, 25
        in: query
        name: Source Group ID
        required: false
        schema: {type: string}
      - description: If True, the operator 'ANY_OF' will be applied. If False, 'None_OF'
          will be applied
        example: ''
        in: query
        name: Source Include
        required: false
        schema: {type: boolean}
      - description: JSON String; will overwrite conditions
        example: '{''OR'': [{''AND'': [{''ANY_OF'': {''field'': ''remote1_ip'', ''values'':
          [{''value'': ''1.1.1.1''}, {''value'': ''1.2.1.3''}], ''groups'': [{''value'':
          13}]}}]}]}'
        in: query
        name: Custom Additional Conditions
        required: false
        schema: {type: string}
      - description: 'Must be one of the following: remote1_ip, remote1_proto, remote1_port,
          remote1_dns, remote2_ip, remote2_proto, remote2_port, remote2_dns, account,
          named_pipe, uuid, identity, share, extensions, rdp client name, rdp client
          token, keyboard name.'
        example: remote1_ip
        in: query
        name: Additional Field
        required: false
        schema: {type: string}
      - description: Value or CSV of multiple values
        example: 1.1.1.1, 2.2.2.2
        in: query
        name: Source Values
        required: false
        schema: {type: string}
      - description: Field group ID, or CSV of names, for applying triage rule to
          entire group. Only fields remote1_ip, remote2_ip, remote1_dns and remote2_dns
          support triaging on groups.
        example: ''
        in: query
        name: Additional Group ID
        required: false
        schema: {type: string}
      - description: If True, the operator 'ANY_OF' will be applied. If False, 'None_OF'
          will be applied
        example: ''
        in: query
        name: Additional Include
        required: false
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Vectra Cognito Create Triage Rule
  tmp1:
    post:
      description: Ingest detections from Vectra Cognito
      parameters:
      - description: Optional detection ID to grab only a specific detection
        example: '1'
        in: body
        name: Detection ID
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Vectra Cognito Get Detections
  tmp2:
    post:
      description: Vectra Cognito Get all hosts
      parameters:
      - description: Optional Host ID to grab only a specific host
        example: '1'
        in: body
        name: Host ID
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Vectra Cognito Get Hosts
  tmp3:
    post:
      description: Vectra Cognito Get PCAP from detection ID
      parameters:
      - description: The detection ID for which PCAP data to retrieve
        example: ''
        in: body
        name: Detection ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Vectra Cognito Get PCAP
  tmp4:
    post:
      description: Get all Triage Rules from Vectra Cognito
      parameters:
      - description: Optional triage rule ID to grab only a specific triage rule
        example: '1'
        in: body
        name: Triage Rule ID
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: Vectra Cognito Get Triage Rules
