info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Claroty Claroty
  title: Claroty Claroty
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Get alerts from Claroty
      parameters:
      - description: Limit the number of results returned
        example: ''
        in: body
        name: Limit
        required: false
        schema: {type: string}
      - description: Filter alerts by severity score, numerical value 1+
        example: ''
        in: body
        name: Severity Filter
        required: false
        schema: {type: string}
      - description: Filter alerts by associated timestamp, format looks like 2020-07-21T17:45:37+00:00
        example: '2020-07-21T17:45:37+00:00'
        in: query
        name: Timestamp Filter
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Claroty Get Alerts
  tmp1:
    post:
      description: Get CVSS by asset and site ID
      parameters:
      - description: ''
        example: '2'
        in: body
        name: Asset ID
        required: true
        schema: {type: string}
      - description: ''
        example: '1'
        in: body
        name: Site ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Claroty Get CVSS
  tmp2:
    post:
      description: Get Assets from Claroty
      parameters:
      - description: Limit the number of results returned
        example: ''
        in: body
        name: Limit
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Claroty Get Assets
  tmp3:
    post:
      description: Resolve alerts as the assed defined user
      parameters:
      - description: Alert Resource IDs like '31-1,29-1'
        example: 30-1
        in: query
        name: Alert Resource IDs (CSV)
        required: true
        schema: {type: string}
      - description: Optional comment to add when resolving alerts.
        example: ''
        in: query
        name: Comment
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Claroty Resolve Alerts
