{
  "openapi": "3.0.0",
  "info": {
    "version": "3.0.0",
    "title": "disease.sh Docs - An open API for disease-related statistics",
    "description": "Third Party API for reliable global disease information",
    "license": {
      "name": "GNU V3",
      "url": "https://github.com/disease-sh/API/blob/master/LICENSE"
    }
  },
  "schemes": [
    "https",
    "http"
  ],
  "host": "disease.sh",
  "basePath": "/",
  "tags": [
    {
      "name": "COVID-19: Worldometers",
      "description": "(COVID-19 data sourced from Worldometers, updated every 10 minutes)"
    },
    {
      "name": "COVID-19: JHUCSSE",
      "description": "(COVID-19 data sourced from Johns Hopkins University, updated every 10 minutes)"
    },
    {
      "name": "COVID-19: NYT",
      "description": "(COVID-19 data sourced from the New York Times, updated every 24 hours)"
    },
    {
      "name": "COVID-19: Apple",
      "description": "(COVID-19 related mobility trend data from Apple, updated every 24 hours)"
    },
    {
      "name": "COVID-19: Government",
      "description": "(COVID-19 individual government reported data, updated every 24 hours)"
    },
    {
      "name": "COVID-19: Vaccine",
      "description": "(COVID-19 vaccine trial data from raps.org, updated every 24 hours)"
    },
    {
      "name": "COVID-19: Therapeutics",
      "description": "(COVID-19 therapeutic trial data from raps.org, updated every 24 hours)"
    },
    {
      "name": "COVID-19: Variants",
      "description": "(COVID-19 data from The European Surveillance System -TESSy, provided by [Austria, Belgium, Bulgaria, Croatia, Cyprus, Czechia, Denmark, Estonia, Finland, France, Germany, Greece, Hungary, Iceland, Ireland, Italy, Latvia, Liechtenstein, Lithuania, Luxembourg, Malta, Netherlands, Norway, Poland, Portugal, Romania, Slovakia, Slovenia, Spain and Sweden]  https://www.ecdc.europa.eu and released by ECDC updated every week)"
    },
    {
      "name": "Influenza: CDC",
      "description": "(Influenza data reported by the United States CDC, updated every 24 hours)"
    }
  ],
  "consumes": [
    "application/json"
  ],
  "produces": [
    "application/json"
  ],
  "paths": {
    "/v3/covid-19/all": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "twoDaysAgo",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported two days ago",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get global COVID-19 totals for today, yesterday and two days ago",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidAll"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/states": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "sort",
            "in": "query",
            "enum": [
              "cases",
              "todayCases",
              "deaths",
              "todayDeaths",
              "active"
            ],
            "description": "Provided a key (e.g. cases, todayCases, deaths, active), result will be sorted from greatest to least",
            "type": "string"
          },
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for all US States",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidStates"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/states/{states}": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "states",
            "in": "path",
            "required": true,
            "description": "State name or comma separated names spelled correctly",
            "type": "string"
          },
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for specific US State(s)",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidState"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/continents": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "twoDaysAgo",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported two days ago",
            "type": "string"
          },
          {
            "name": "sort",
            "in": "query",
            "enum": [
              "cases",
              "todayCases",
              "deaths",
              "todayDeaths",
              "recovered",
              "active",
              "critical",
              "casesPerOneMillion",
              "deathsPerOneMillion"
            ],
            "description": "Provided a key (e.g. cases, todayCases, deaths, recovered, active), results will be sorted from greatest to least",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for all continents",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidContinents"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/continents/{continent}": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "continent",
            "in": "path",
            "required": true,
            "description": "Continent name",
            "type": "string"
          },
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "twoDaysAgo",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported two days ago",
            "type": "string"
          },
          {
            "name": "strict",
            "in": "query",
            "enum": [
              "true",
              "false"
            ],
            "default": "true",
            "description": "Setting to false gives you the ability to fuzzy search continents (i.e. Oman vs. rOMANia)",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for a specific continent",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidContinent"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/countries": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "twoDaysAgo",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported two days ago",
            "type": "string"
          },
          {
            "name": "sort",
            "in": "query",
            "enum": [
              "cases",
              "todayCases",
              "deaths",
              "todayDeaths",
              "recovered",
              "active",
              "critical",
              "casesPerOneMillion",
              "deathsPerOneMillion"
            ],
            "description": "Provided a key (e.g. cases, todayCases, deaths, recovered, active), the result will be sorted from greatest to least",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for all countries",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidCountries"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/countries/{country}": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "country",
            "in": "path",
            "required": true,
            "description": "A country name, iso2, iso3, or country ID code",
            "type": "string"
          },
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "twoDaysAgo",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported two days ago",
            "type": "string"
          },
          {
            "name": "strict",
            "in": "query",
            "enum": [
              "true",
              "false"
            ],
            "default": "true",
            "description": "Setting to false gives you the ability to fuzzy search countries (i.e. Oman vs. rOMANia)",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for a specific country",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidCountry"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/countries/{countries}": {
      "get": {
        "tags": [
          "COVID-19: Worldometers"
        ],
        "parameters": [
          {
            "name": "countries",
            "in": "path",
            "required": true,
            "description": "Multiple country names, iso2, iso3, or country IDs separated by commas",
            "type": "string"
          },
          {
            "name": "yesterday",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported a day ago",
            "type": "string"
          },
          {
            "name": "twoDaysAgo",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "Queries data reported two days ago",
            "type": "string"
          },
          {
            "name": "allowNull",
            "in": "query",
            "enum": [
              "true",
              "false",
              "1",
              "0"
            ],
            "description": "By default, if a value is missing, it is returned as 0. This allows nulls to be returned",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for a specific set of countries",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidCountries"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/jhucsse": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "summary": "Get COVID-19 totals for all countries and their provinces",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidJHUCountries"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/jhucsse/counties": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "summary": "Get COVID-19 totals for all US counties",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidJHUCounties"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/jhucsse/counties/{county}": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "county",
            "in": "path",
            "required": true,
            "description": "Name of any county in the USA. All counties are listed in the /v3/covid-19/jhucsse/counties/ endpoint",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 totals for a specific county",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidJHUCounties"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "lastdays",
            "in": "query",
            "default": 30,
            "description": "Number of days to return. Use 'all' for the full data set (e.g. 15, all, 24)",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 time series data for all countries and their provinces",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistorical"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical/all": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "lastdays",
            "in": "query",
            "default": 30,
            "description": "Number of days to return. Use 'all' for the full data set (e.g. 15, all, 24)",
            "type": "string"
          }
        ],
        "summary": "Get global accumulated COVID-19 time series data",
        "responses": {
          "200": {
            "description": "Status Ok",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistoricalAll"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical/{country}": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "country",
            "in": "path",
            "required": true,
            "description": "A country name, iso2, iso3, or country ID code",
            "type": "string"
          },
          {
            "name": "lastdays",
            "in": "query",
            "default": 30,
            "description": "Number of days to return. Use 'all' for the full data set (e.g. 15, all, 24)",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 time series data for a specific country",
        "responses": {
          "200": {
            "description": "Status Ok",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistoricalCountry"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical/{countries}": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "countries",
            "in": "path",
            "required": true,
            "description": "Multiple country names, iso2, iso3, or country IDs separated by commas",
            "type": "string"
          },
          {
            "name": "lastdays",
            "in": "query",
            "default": 30,
            "description": "Number of days to return. Use 'all' for the full data set (e.g. 15, all, 24)",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 time series data for a specific set of countries",
        "responses": {
          "200": {
            "description": "Status Ok",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistoricalCountries"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical/{country}/{province}": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "country",
            "in": "path",
            "required": true,
            "description": "A country name, iso2, iso3, or country ID code",
            "type": "string"
          },
          {
            "name": "province",
            "in": "path",
            "required": true,
            "description": "Province name. All available names can be found in the /v3/covid-19/historical/{query} endpoint",
            "type": "string"
          },
          {
            "name": "lastdays",
            "in": "query",
            "default": 30,
            "description": "Number of days to return. Use 'all' for the full data set (e.g. 15, all, 24)",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 time series data for a specific province in a country",
        "responses": {
          "200": {
            "description": "Status Ok",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistoricalProvince"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical/{country}/{provinces}": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "country",
            "in": "path",
            "required": true,
            "description": "A country name, iso2, iso3, or country ID code",
            "type": "string"
          },
          {
            "name": "provinces",
            "in": "path",
            "required": true,
            "description": "Provinces spelled correctly separated by ',' or '|' delimiters, never both in the same query",
            "type": "string"
          },
          {
            "name": "lastdays",
            "in": "query",
            "default": 30,
            "description": "Number of days to return. Use 'all' for the full data set (e.g. 15, all, 24)",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 time series data for a set of provinces in a country",
        "responses": {
          "200": {
            "description": "Status Ok",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistoricalProvinces"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical/usacounties": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "summary": "Get all possible US States to query the /historical/usacounties/{state} endpoint with",
        "description": "Returns a list of US States and provinces",
        "responses": {
          "200": {
            "description": "Status OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistoricalUSCounties"
                }   
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/historical/usacounties/{state}": {
      "get": {
        "tags": [
          "COVID-19: JHUCSSE"
        ],
        "parameters": [
          {
            "name": "state",
            "in": "path",
            "required": true,
            "description": "US state name, validated in the array returned from the /v3/covid-19/historical/usacounties endpoint",
            "type": "string"
          },
          {
            "name": "lastdays",
            "in": "query",
            "default": 30,
            "description": "Number of days to return. Use 'all' for the full data set (e.g. 15, all, 24)",
            "type": "string"
          }
        ],
        "summary": "Get COVID-19 time series data for all counties in a specified US state",
        "responses": {
          "200": {
            "description": "Status Ok",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/covidHistoricalUSCounty"
                }
              }
            }
          }
        }
      }
    },
    "/v3/covid-19/nyt/states": {
      "get": {
        "tags": [
          "COVID-19: NYT"
        ],
        "parameters": [
          {
            "name": "lastdays",
