  "servers": [
    {
      "url": "https://api.medusa-commerce.com/admin"
    }
  ],
  "paths": {
    "/apps": {
      "post": {
        "operationId": "PostApps",
        "summary": "Generates a token for an application.",
        "description": "Generates a token for an application.",
        "x-authenticated": true,
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "required": [
                  "application_name",
                  "state",
                  "code"
                ],
                "properties": {
                  "application_name": {
                    "type": "string",
                    "description": "Name of the application for the token to be generated for."
                  },
                  "state": {
                    "type": "string",
                    "description": "State of the application."
                  },
                  "code": {
                    "type": "string",
                    "description": "The code for the generated token."
                  }
                }
              }
            }
          }
        },
        "tags": [
          "Apps"
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "apps": {
                      "$ref": "#/components/schemas/OAuth"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "get": {
        "operationId": "GetApps",
        "summary": "List applications",
        "description": "Retrieve a list of applications.",
        "x-authenticated": true,
        "tags": [
          "Apps"
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "collection": {
                      "$ref": "#/components/schemas/OAuth"
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/auth": {
      "post": {
        "operationId": "PostAuth",
        "summary": "Authenticate a User",
        "x-authenticated": false,
        "description": "Logs a User in and authorizes them to manage Store settings.",
        "parameters": [],
        "tags": [
          "Auth"
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "user": {
                      "$ref": "#/components/schemas/user"
                    }
                  }
                }
              }
            }
          }
        },
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "email",
                  "password"
                ],
                "properties": {
                  "email": {
                    "type": "string",
                    "description": "The User's email."
                  },
                  "password": {
                    "type": "string",
                    "description": "The User's password."
                  }
                }
              }
            }
          }
        }
      },
      "get": {
        "operationId": "GetAuth",
        "summary": "Get Session",
        "x-authenticated": true,
        "description": "Gets the currently logged in User.",
        "tags": [
          "Auth"
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "user": {
                      "$ref": "#/components/schemas/user"
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/batch-jobs/{id}/cancel": {
      "post": {
        "operationId": "PostBatchJobsBatchJobCancel",
        "summary": "Marks a batch job as canceled",
