        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "properties": {
                  "product_ids": {
                    "description": "An array of Product IDs to remove from the Product Collection.",
                    "type": "array",
                    "items": {
                      "properties": {
                        "id": {
                          "description": "The ID of a Product to remove from the Product Collection.",
