  "x-hasEquivalentPaths": true,
  "paths": {
    "/assets/{assetId}/associate": {
      "post": {
        "operationId": "AssociateAssets",
        "description": "Associates a child asset with the given parent asset through a hierarchy defined in the parent asset's model. For more information, see <a href=\"https://docs.aws.amazon.com/iot-sitewise/latest/userguide/add-associated-assets.html\">Associating assets</a> in the <i>AWS IoT SiteWise User Guide</i>.",
        "responses": {
          "200": { "description": "Success" },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "ResourceNotFoundException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceNotFoundException"
                }
              }
            }
          },
          "482": {
            "description": "InternalFailureException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalFailureException"
                }
              }
            }
          },
          "483": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "484": {
            "description": "ThrottlingException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottlingException" }
              }
            }
          },
          "485": {
            "description": "ConflictingOperationException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ConflictingOperationException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "assetId",
            "in": "path",
            "required": true,
            "description": "The ID of the parent asset.",
            "schema": {
              "type": "string",
              "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$",
              "minLength": 36,
              "maxLength": 36
            }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["hierarchyId", "childAssetId"],
                "properties": {
                  "hierarchyId": {
                    "description": "The ID of a hierarchy in the parent asset's model. Hierarchies allow different groupings of assets to be formed that all come from the same asset model. For more information, see <a href=\"https://docs.aws.amazon.com/iot-sitewise/latest/userguide/asset-hierarchies.html\">Asset hierarchies</a> in the <i>AWS IoT SiteWise User Guide</i>.",
                    "type": "string",
                    "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$",
                    "minLength": 36,
                    "maxLength": 36
                  },
                  "childAssetId": {
                    "description": "The ID of the child asset to be associated.",
                    "type": "string",
                    "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$",
                    "minLength": 36,
                    "maxLength": 36
                  },
                  "clientToken": {
                    "description": "A unique case-sensitive identifier that you can provide to ensure the idempotency of the request. Don't reuse this client token if a new idempotent request is required.",
                    "type": "string",
                    "pattern": "\\S{36,64}",
                    "minLength": 36,
                    "maxLength": 64
                  }
                }
              }
            }
          }
        },
        "summary": "Associate Assets"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/projects/{projectId}/assets/associate": {
