                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["entries"],
                "properties": {
                  "entries": {
                    "description": "The list of asset property value entries for the batch put request. You can specify up to 10 entries per request.",
                    "type": "array",
                    "items": {
                      "$ref": "#/components/schemas/PutAssetPropertyValueEntry"
                    }
                  }
                }
              }
            }
          }
        },
