    "/access-policies": {
      "post": {
        "operationId": "CreateAccessPolicy",
        "description": "Creates an access policy that grants the specified AWS Single Sign-On user or group access to the specified AWS IoT SiteWise Monitor portal or project resource.",
        "responses": {
          "201": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/CreateAccessPolicyResponse"
                }
