              }
            }
          },
          "484": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "accessPolicyIdentity",
                  "accessPolicyResource",
                  "accessPolicyPermission"
                ],
                "properties": {
                  "accessPolicyIdentity": {
                    "description": "<p>Contains an AWS SSO identity ID for a user or group.</p> <note> <p>Currently, you can't use AWS APIs to retrieve AWS SSO identity IDs. You can find the AWS SSO identity IDs in the URL of user and group pages in the <a href=\"https://console.aws.amazon.com/singlesignon\">AWS SSO console</a>.</p> </note>",
                    "type": "object",
                    "properties": {
                      "user": {
                        "allOf": [
                          { "$ref": "#/components/schemas/UserIdentity" },
                          { "description": "A user identity." }
                        ]
                      },
                      "group": {
                        "allOf": [
                          { "$ref": "#/components/schemas/GroupIdentity" },
                          { "description": "A group identity." }
                        ]
                      },
                      "iamUser": {
                        "allOf": [
                          { "$ref": "#/components/schemas/IAMUserIdentity" },
                          { "description": "An IAM user identity." }
                        ]
                      },
                      "iamRole": {
                        "allOf": [
                          { "$ref": "#/components/schemas/IAMRoleIdentity" },
                          { "description": "An IAM role identity." }
                        ]
                      }
                    }
                  },
                  "accessPolicyResource": {
                    "description": "Contains an AWS IoT SiteWise Monitor resource ID for a portal or project.",
                    "type": "object",
                    "properties": {
                      "portal": {
                        "allOf": [
                          { "$ref": "#/components/schemas/PortalResource" },
                          { "description": "A portal resource." }
                        ]
                      },
                      "project": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ProjectResource" },
                          { "description": "A project resource." }
                        ]
                      }
                    }
                  },
                  "accessPolicyPermission": {
                    "description": "The permission level for this access policy. Note that a project <code>ADMINISTRATOR</code> is also known as a project owner.",
                    "type": "string",
                    "enum": ["ADMINISTRATOR", "VIEWER"]
                  },
                  "clientToken": {
                    "description": "A unique case-sensitive identifier that you can provide to ensure the idempotency of the request. Don't reuse this client token if a new idempotent request is required.",
                    "type": "string",
                    "pattern": "\\S{36,64}",
                    "minLength": 36,
                    "maxLength": 64
