{
  "openapi": "3.0.0",
  "info": {
    "title": "Tezos RPC",
    "description": "Tezos client RPC API.",
    "version": "15.0"
  },
  "paths": {
    "/": {
      "get": {
        "description": "All the information about a block. The associated metadata may not be present depending on the history mode and block's distance from the head.",
        "parameters": [
          {
            "name": "force_metadata",
            "in": "query",
            "description": "DEPRECATED: Forces to recompute the operations metadata if it was considered as too large.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "metadata",
            "in": "query",
            "description": "defines the way metadata are queried Specifies whether or not if the operations metadata should be returned. To get the metadata, even if it is needed to recompute them, use \"always\". To avoid getting the metadata, use \"never\". By default, the metadata will be returned depending on the node's metadata size limit policy.",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "protocol": {
                      "type": "string",
                      "enum": [
                        "ProtoGenesisGenesisGenesisGenesisGenesisGenesk612im"
                      ]
                    },
                    "chain_id": {
                      "$ref": "#/components/schemas/Chain_id"
                    },
                    "hash": {
                      "$ref": "#/components/schemas/block_hash"
                    },
                    "header": {
                      "$ref": "#/components/schemas/raw_block_header"
                    },
                    "metadata": {
                      "$ref": "#/components/schemas/block_header_metadata"
                    },
                    "operations": {
                      "type": "array",
                      "items": {
                        "type": "array",
                        "items": {
                          "$ref": "#/components/schemas/operation"
                        }
                      }
                    }
                  },
                  "required": [
                    "protocol",
                    "chain_id",
                    "hash",
                    "header",
                    "operations"
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "description": "The full list of errors is available with the global RPC `GET errors`"
                }
              }
            }
          }
        }
      }
    },
    "/context/big_maps/{big_map_id}": {
      "get": {
        "description": "Get the (optionally paginated) list of values in a big map. Order of values is unspecified, but is guaranteed to be consistent.",
        "parameters": [
          {
            "name": "big_map_id",
            "in": "path",
            "description": "A big map identifier",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "offset",
            "in": "query",
            "description": "A non-negative integer (greater than or equal to 0). Skip the first [offset] values. Useful in combination with [length] for pagination.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "length",
            "in": "query",
            "description": "A non-negative integer (greater than or equal to 0). Only retrieve [length] values. Useful in combination with [offset] for pagination.",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/micheline.015-PtLimaPt.michelson_v1.expression"
                  }
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "description": "The full list of errors is available with the global RPC `GET errors`"
                }
              }
            }
          }
        }
      }
    },
    "/context/big_maps/{big_map_id}/{script_expr}": {
      "get": {
        "description": "Access the value associated with a key in a big map.",
        "parameters": [
          {
            "name": "big_map_id",
            "in": "path",
            "description": "A big map identifier",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "script_expr",
            "in": "path",
            "description": "script_expr (Base58Check-encoded)",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/micheline.015-PtLimaPt.michelson_v1.expression"
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "description": "The full list of errors is available with the global RPC `GET errors`"
                }
              }
            }
          }
        }
      }
    },
    "/context/big_maps/{big_map_id}/{script_expr}/normalized": {
      "post": {
        "description": "Access the value associated with a key in a big map, normalize the output using the requested unparsing mode.",
        "parameters": [
          {
            "name": "big_map_id",
            "in": "path",
            "description": "A big map identifier",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "script_expr",
            "in": "path",
            "description": "script_expr (Base58Check-encoded)",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "unparsing_mode": {
                    "oneOf": [
                      {
                        "title": "Readable",
                        "type": "string",
                        "enum": [
                          "Readable"
                        ]
                      },
                      {
                        "title": "Optimized",
                        "type": "string",
                        "enum": [
                          "Optimized"
                        ]
                      },
                      {
                        "title": "Optimized_legacy",
                        "type": "string",
                        "enum": [
                          "Optimized_legacy"
                        ]
                      }
                    ]
                  }
                },
                "required": [
                  "unparsing_mode"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/micheline.015-PtLimaPt.michelson_v1.expression"
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "description": "The full list of errors is available with the global RPC `GET errors`"
                }
              }
            }
          }
        }
      }
    },
    "/context/cache/contracts/all": {
      "get": {
        "description": "Return the list of cached contracts",
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "array",
                    "items": {
                      "oneOf": [
                        {
                          "$ref": "#/components/schemas/Contract_hash"
                        },
                        {
                          "type": "integer",
                          "minimum": -1073741824,
                          "maximum": 1073741823
                        }
                      ]
                    }
                  }
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "description": "The full list of errors is available with the global RPC `GET errors`"
                }
              }
            }
          }
        }
      }
    },
    "/context/cache/contracts/rank": {
      "post": {
        "description": "Return the number of cached contracts older than the provided contract",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "title": "A contract handle -- originated account",
                "description": "A contract notation as given to an RPC or inside scripts. Can be a base58 originated contract hash.",
                "oneOf": [
                  {
                    "$ref": "#/components/schemas/unistring"
                  }
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "nullable": true,
                  "oneOf": [
                    {
                      "title": "Some",
                      "type": "integer",
                      "minimum": -1073741824,
                      "maximum": 1073741823
                    }
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "description": "The full list of errors is available with the global RPC `GET errors`"
                }
              }
            }
          }
        }
      }
    },
    "/context/cache/contracts/size": {
      "get": {
        "description": "Return the size of the contract cache",
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "integer",
                  "minimum": -1073741824,
                  "maximum": 1073741823
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "description": "The full list of errors is available with the global RPC `GET errors`"
