      "__stringPatternWS": { "type": "string", "pattern": "^[\\w\\s]*$" },
      "AudioDescription": {
        "type": "object",
        "properties": {
          "AudioChannelTaggingSettings": {
            "allOf": [
              { "$ref": "#/components/schemas/AudioChannelTaggingSettings" },
              {
                "xml": { "name": "audioChannelTaggingSettings" },
                "description": "When you mimic a multi-channel audio layout with multiple mono-channel tracks, you can tag each channel layout manually. For example, you would tag the tracks that contain your left, right, and center audio with Left (L), Right (R), and Center (C), respectively. When you don't specify a value, MediaConvert labels your track as Center (C) by default. To use audio layout tagging, your output must be in a QuickTime (.mov) container; your audio codec must be AAC, WAV, or AIFF; and you must set up your audio track to have only one channel."
              }
            ]
          },
          "AudioNormalizationSettings": {
            "allOf": [
              { "$ref": "#/components/schemas/AudioNormalizationSettings" },
              {
                "xml": { "name": "audioNormalizationSettings" },
                "description": "Advanced audio normalization settings. Ignore these settings unless you need to comply with a loudness standard."
              }
            ]
          },
          "AudioSourceName": {
            "allOf": [
              { "$ref": "#/components/schemas/__string" },
              {
                "xml": { "name": "audioSourceName" },
                "description": "Specifies which audio data to use from each input. In the simplest case, specify an \"Audio Selector\":#inputs-audio_selector by name based on its order within each input. For example if you specify \"Audio Selector 3\", then the third audio selector will be used from each input. If an input does not have an \"Audio Selector 3\", then the audio selector marked as \"default\" in that input will be used. If there is no audio selector marked as \"default\", silence will be inserted for the duration of that input. Alternatively, an \"Audio Selector Group\":#inputs-audio_selector_group name may be specified, with similar default/silence behavior. If no audio_source_name is specified, then \"Audio Selector 1\" will be chosen automatically."
              }
            ]
          },
          "AudioType": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin0Max255" },
              {
                "xml": { "name": "audioType" },
                "description": "Applies only if Follow Input Audio Type is unchecked (false). A number between 0 and 255. The following are defined in ISO-IEC 13818-1: 0 = Undefined, 1 = Clean Effects, 2 = Hearing Impaired, 3 = Visually Impaired Commentary, 4-255 = Reserved."
              }
            ]
          },
          "AudioTypeControl": {
