            "allOf": [
              { "$ref": "#/components/schemas/__stringMin1" },
              {
                "xml": { "name": "manifestNameModifier" },
                "description": "Specify a name modifier that the service adds to the name of this manifest to make it different from the file names of the other main manifests in the output group. For example, say that the default main manifest for your Microsoft Smooth group is film-name.ismv. If you enter \"-no-premium\" for this setting, then the file name the service generates for this top-level manifest is film-name-no-premium.ismv."
              }
            ]
          },
          "SelectedOutputs": {
            "allOf": [
              { "$ref": "#/components/schemas/__listOf__stringMin1" },
