      "__integerMin0Max0": { "type": "integer", "minimum": 0, "maximum": 0 },
      "NoiseFilterPostTemporalSharpening": {
        "type": "string",
        "enum": ["DISABLED", "ENABLED", "AUTO"],
        "description": "Optional. When you set Noise reducer (noiseReducer) to Temporal (TEMPORAL), you can use this setting to apply sharpening. The default behavior, Auto (AUTO), allows the transcoder to determine whether to apply filtering, depending on input type and quality. When you set Noise reducer to Temporal, your output bandwidth is reduced. When Post temporal sharpening is also enabled, that bandwidth reduction is smaller."
      },
      "NoiseReducerFilter": {
        "type": "string",
        "enum": [
          "BILATERAL",
          "MEAN",
          "GAUSSIAN",
          "LANCZOS",
          "SHARPEN",
          "CONSERVE",
          "SPATIAL",
          "TEMPORAL"
        ],
        "description": "Use Noise reducer filter (NoiseReducerFilter) to select one of the following spatial image filtering functions. To use this setting, you must also enable Noise reducer (NoiseReducer). * Bilateral preserves edges while reducing noise. * Mean (softest), Gaussian, Lanczos, and Sharpen (sharpest) do convolution filtering. * Conserve does min/max noise reduction. * Spatial does frequency-domain filtering based on JND principles. * Temporal optimizes video quality for complex motion."
      },
      "NoiseReducerFilterSettings": {
        "type": "object",
        "properties": {
          "Strength": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin0Max3" },
              {
                "xml": { "name": "strength" },
                "description": "Relative strength of noise reducing filter. Higher values produce stronger filtering."
              }
            ]
          }
        },
        "description": "Settings for a noise reducer filter"
      },
      "NoiseReducerSpatialFilterSettings": {
        "type": "object",
        "properties": {
          "PostFilterSharpenStrength": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin0Max3" },
              {
                "xml": { "name": "postFilterSharpenStrength" },
                "description": "Specify strength of post noise reduction sharpening filter, with 0 disabling the filter and 3 enabling it at maximum strength."
              }
            ]
          },
          "Speed": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMinNegative2Max3" },
              {
                "xml": { "name": "speed" },
                "description": "The speed of the filter, from -2 (lower speed) to 3 (higher speed), with 0 being the nominal value."
              }
            ]
          },
          "Strength": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin0Max16" },
              {
                "xml": { "name": "strength" },
                "description": "Relative strength of noise reducing filter. Higher values produce stronger filtering."
              }
            ]
          }
        },
        "description": "Noise reducer filter settings for spatial filter."
      },
      "NoiseReducerTemporalFilterSettings": {
        "type": "object",
        "properties": {
          "AggressiveMode": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin0Max4" },
              {
                "xml": { "name": "aggressiveMode" },
                "description": "Use Aggressive mode for content that has complex motion. Higher values produce stronger temporal filtering. This filters highly complex scenes more aggressively and creates better VQ for low bitrate outputs."
              }
            ]
          },
          "PostTemporalSharpening": {
            "allOf": [
              {
                "$ref": "#/components/schemas/NoiseFilterPostTemporalSharpening"
              },
              {
                "xml": { "name": "postTemporalSharpening" },
                "description": "Optional. When you set Noise reducer (noiseReducer) to Temporal (TEMPORAL), you can use this setting to apply sharpening. The default behavior, Auto (AUTO), allows the transcoder to determine whether to apply filtering, depending on input type and quality. When you set Noise reducer to Temporal, your output bandwidth is reduced. When Post temporal sharpening is also enabled, that bandwidth reduction is smaller."
              }
            ]
          },
          "Speed": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMinNegative1Max3" },
              {
                "xml": { "name": "speed" },
                "description": "The speed of the filter (higher number is faster). Low setting reduces bit rate at the cost of transcode time, high setting improves transcode time at the cost of bit rate."
              }
            ]
          },
          "Strength": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin0Max16" },
              {
                "xml": { "name": "strength" },
                "description": "Specify the strength of the noise reducing filter on this output. Higher values produce stronger filtering. We recommend the following value ranges, depending on the result that you want: * 0-2 for complexity reduction with minimal sharpness loss * 2-8 for complexity reduction with image preservation * 8-16 for a high level of complexity reduction"
              }
            ]
          }
        },
        "description": "Noise reducer filter settings for temporal filter."
