        "parameters": [
          {
            "name": "filename",
            "in": "path",
            "description": "Filename to remove",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Response when successfuly uploaded.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "string",
                  "example": "Success"
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        },
        "tags": [
          "Image Definitions"
        ]
      }
    }
  },
