          "label": {
            "type": "string"
          },
          "slot": {
            "type": "integer",
            "minimum": 0
          }
        }
      },
      "RESTNode": {
        "description": "Special exchange format for the node details in JSON format.",
        "required": [
          "id",
          "data"
        ],
        "properties": {
          "id": {
            "type": "string"
          },
          "data": {
            "type": "object",
            "properties": {
              "x": {
                "type": "integer"
              },
              "y": {
                "type": "integer"
              },
              "label": {
                "type": "string",
                "description": "A label for the specified node"
              },
              "node_definition": {
                "type": "string",
