            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "mail": {
                    "type": "string"
                  },
                  "user_name": {
                    "type": "string"
                  }
                },
                "required": [
                  "mail",
                  "user_name"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Success Reset Password"
