          "default": [
            "fresh",
            "stale",
            "unknown"
          ]
        },
        "description": "Culling states of the hosts. Default: fresh,stale,unknown"
      },
      "stalenessNoDefaultsParam": {
        "name": "staleness",
        "in": "query",
        "required": false,
        "schema": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": [
              "fresh",
              "stale",
              "stale_warning",
              "unknown"
            ]
          }
        },
        "description": "Culling states of the hosts."
      },
      "tagsParam": {
        "name": "tags",
