                            {
                              "type": "string"
                            },
                            {
                              "type": "object",
                              "properties": {
                                "and": {
                                  "type": "array",
                                  "items": {
                                    "$ref": "#/paths/~1module~1{moduleCode}/get/responses/200/content/application~1json/schema/properties/data/properties/prereqTree"
                                  }
                                },
