          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "calls": {
                      "items": {
                        "format": "date", 
                        "type": "string"
                      }, 
                      "type": "array"
                    }, 
                    "mds": {
                      "items": {
                        "format": "date", 
                        "type": "string"
                      }, 
                      "type": "array"
