                    "sms": {
                      "items": {
                        "format": "date", 
                        "type": "string"
                      }, 
                      "type": "array"
                    }, 
                    "topups": {
                      "items": {
                        "format": "date", 
                        "type": "string"
                      }, 
                      "type": "array"
                    }
                  }, 
                  "type": "object"
                }
              }
            }, 
            "description": "Dates available for each event type."
          }, 
          "401": {
            "description": "Unauthorized."
          }, 
          "403": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object"
                }
              }
            }, 
            "description": "No access with this token."
          }, 
          "500": {
            "description": "Server error."
          }
        }, 
        "summary": "Get the dates available to query over."
      }
    }, 
