    "/api/0/poll/<query_id>": {
      "get": {
        "operationId": "query.poll_query.get", 
        "parameters": [
          {
            "in": "path", 
            "name": "query_id", 
            "required": true, 
            "schema": {
              "type": "string"
            }
          }
        ], 
        "responses": {
          "202": {
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "msg": {
                      "type": "string"
                    }, 
                    "progress": {
                      "schema": {
                        "eligible": {
                          "type": "integer"
                        }, 
                        "queued": {
                          "type": "integer"
                        }, 
                        "running": {
                          "type": "integer"
                        }
                      }, 
                      "type": "object"
                    }, 
                    "status": {
                      "enum": [
                        "executing", 
                        "queued"
                      ], 
                      "type": "string"
                    }
                  }, 
                  "type": "object"
                }
              }
            }, 
            "description": "Request accepted."
          }, 
          "303": {
            "description": "Data ready.", 
            "headers": {
              "Location": {
                "description": "URL to download data", 
                "schema": {
                  "format": "url", 
                  "type": "string"
