        "operationId": "query.run_query.post", 
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/FlowmachineQuerySchema"
              }
            }
          }, 
          "required": true
        }, 
        "responses": {
          "202": {
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "progress": {
                      "schema": {
                        "eligible": {
                          "type": "integer"
                        }, 
                        "queued": {
                          "type": "integer"
                        }, 
                        "running": {
                          "type": "integer"
                        }
                      }, 
                      "type": "object"
                    }, 
                    "query_id": {
                      "type": "string"
                    }
                  }, 
                  "type": "object"
                }
              }
            }, 
            "description": "Request accepted.", 
            "headers": {
              "Location": {
                "description": "URL to poll for status", 
                "schema": {
                  "format": "url", 
                  "type": "string"
                }
              }
            }
          }, 
          "400": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object"
                }
              }
            }, 
            "description": "Query spec could not be run.."
