    "/present-proof/records": {
      "get": {
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/V10PresentationExchangeList"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "in": "query",
            "name": "connection_id",
            "required": false,
            "description": "Connection identifier",
            "example": "3fa85f64-5717-4562-b3fc-2c963f66afa6",
            "schema": {
              "type": "string",
              "format": "uuid"
            }
          },
          {
            "in": "query",
            "name": "role",
            "required": false,
            "description": "Role assigned in presentation exchange",
            "schema": {
              "type": "string",
              "enum": ["prover", "verifier"]
            }
          },
          {
            "in": "query",
            "name": "state",
            "required": false,
            "description": "Presentation exchange state",
            "schema": {
              "type": "string",
              "enum": [
                "proposal_sent",
                "proposal_received",
                "request_sent",
                "request_received",
                "presentation_sent",
                "presentation_received",
                "verified",
                "presentation_acked"
              ]
            }
          },
          {
            "in": "query",
            "name": "thread_id",
            "required": false,
            "description": "Thread identifier",
            "example": "3fa85f64-5717-4562-b3fc-2c963f66afa6",
            "schema": {
              "type": "string",
              "format": "uuid"
            }
          }
        ],
        "tags": ["present-proof"],
        "summary": "Fetch all present-proof exchange records"
      }
    },
    "/present-proof/records/{pres_ex_id}": {
      "get": {
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/V10PresentationExchange"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "in": "path",
            "name": "pres_ex_id",
            "required": true,
            "description": "Presentation exchange identifier",
            "example": "3fa85f64-5717-4562-b3fc-2c963f66afa6",
            "schema": {
              "type": "string",
              "pattern": "[a-fA-F0-9]{8}-[a-fA-F0-9]{4}-4[a-fA-F0-9]{3}-[a-fA-F0-9]{4}-[a-fA-F0-9]{12}"
            }
          }
        ],
        "tags": ["present-proof"],
        "summary": "Fetch a single presentation exchange record"
      }
    },
    "/present-proof/records/{pres_ex_id}/credentials": {
      "get": {
        "responses": {},
        "parameters": [
          {
            "in": "path",
            "name": "pres_ex_id",
            "required": true,
            "description": "Presentation exchange identifier",
            "example": "3fa85f64-5717-4562-b3fc-2c963f66afa6",
            "schema": {
              "type": "string",
              "pattern": "[a-fA-F0-9]{8}-[a-fA-F0-9]{4}-4[a-fA-F0-9]{3}-[a-fA-F0-9]{4}-[a-fA-F0-9]{12}"
            }
          },
          {
            "in": "query",
            "name": "count",
            "required": false,
            "description": "Maximum number to retrieve",
            "example": 10,
            "schema": {
              "type": "integer",
              "format": "int32",
              "minimum": 1
            }
          },
          {
            "in": "query",
            "name": "extra_query",
            "required": false,
            "description": "(JSON) object mapping referents to extra WQL queries",
            "example": "{\"0_drink_uuid\": {\"attr::drink::value\": \"martini\"}}",
            "schema": {
              "type": "string",
              "pattern": "^{\\s*\".*?\"\\s*:\\s*{.*?}\\s*(,\\s*\".*?\"\\s*:\\s*{.*?}\\s*)*\\s*}$"
            }
          },
          {
            "in": "query",
            "name": "referent",
            "required": false,
            "description": "Proof request referents of interest, comma-separated",
            "example": "1_name_uuid,2_score_uuid",
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "start",
            "required": false,
            "description": "Start index",
            "example": 0,
            "schema": {
              "type": "integer",
              "format": "int32",
              "minimum": 0
            }
          }
        ],
        "tags": ["present-proof"],
        "summary": "Fetch credentials for a presentation request from wallet"
      }
    },
    "/present-proof/send-proposal": {
      "post": {
        "responses": {
          "200": {
