    "/connections": {
      "get": {
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ConnectionList"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "in": "query",
            "name": "alias",
            "required": false,
            "description": "Alias",
            "example": "Barry",
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "initiator",
            "required": false,
            "description": "Connection initiator",
            "schema": {
              "type": "string",
              "enum": ["self", "external"]
            }
          },
          {
            "in": "query",
            "name": "invitation_key",
            "required": false,
            "description": "invitation key",
            "example": "H3C2AVvLMv6gmMNam3uVAjZpfkcJCwDwnZn6z3wXmqPV",
            "schema": {
              "type": "string",
              "pattern": "^[123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz]{43,44}$"
            }
          },
          {
            "in": "query",
            "name": "my_did",
            "required": false,
            "description": "My DID",
            "example": "WgWxqztrNooG92RXvxSTWv",
            "schema": {
              "type": "string",
              "pattern": "^(did:sov:)?[123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz]{21,22}$"
            }
          },
          {
            "in": "query",
            "name": "state",
            "required": false,
            "description": "Connection state",
            "schema": {
              "type": "string",
              "enum": [
                "init",
                "invitation",
                "request",
                "response",
                "active",
                "error",
                "inactive"
              ]
            }
          },
          {
            "in": "query",
            "name": "their_did",
            "required": false,
            "description": "Their DID",
            "example": "WgWxqztrNooG92RXvxSTWv",
            "schema": {
              "type": "string",
              "pattern": "^(did:sov:)?[123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz]{21,22}$"
            }
          },
          {
            "in": "query",
            "name": "their_role",
            "required": false,
            "description": "Their assigned connection role",
            "example": "Point of contact",
            "schema": {
              "type": "string"
            }
          }
        ],
        "tags": ["connection"],
        "summary": "Query agent-to-agent connections"
      }
    },
    "/connections/{conn_id}": {
      "get": {
        "responses": {
