          "id": {
            "type": "string",
            "example": "commercial-music-service-spotify"
          },
          "label": {
            "type": "string",
            "example": "Spotify"
          },
          "uri": {
            "type": "string",
            "example": "https://open.spotify.com/track/64PdDpMqGafC4axjVZc27T"
          }
        },
        "required": [
          "type",
          "id",
          "label",
          "uri"
        ]
      },
      "Segment": {
        "type": "object",
        "properties": {
          "type": {
            "type": "string",
            "enum": [
              "segment_item"
            ]
          },
          "id": {
            "type": "string",
            "pattern": "^([0-9|b-d|f-h|j-n|p-t|v-z]{8,}|[a-zA-Z0-9_]{1,})$",
            "example": "b08vxtj4"
          },
          "urn": {
            "type": "string",
            "pattern": "^urn:bbc:radio:segment:[a-zA-Z0-9:_]{1,}$",
            "example": "urn:bbc:radio:segment:music|classical:b08vxv2w"
          },
          "segment_type": {
            "type": "string",
            "example": "music|classical"
          },
          "titles": {
            "$ref": "#/components/schemas/SegmentTitles"
          },
          "synopses": {
            "oneOf": [
              {
                "$ref": "#/components/schemas/SegmentSynopses"
              },
              {
                "$ref": "#/components/schemas/NullValue"
              }
            ]
          },
          "image_url": {
            "type": "string",
            "example": "https://ichef.bbci.co.uk/images/ic/{recipe}/p054zzwj.jpg"
          },
          "offset": {
            "$ref": "#/components/schemas/SegmentOffset"
          },
          "uris": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/CommercialLinks"
            }
          }
        },
        "required": [
          "type",
          "id",
          "urn",
          "segment_type",
          "titles",
          "synopses",
          "image_url",
          "offset",
          "uris"
        ]
      },
